import React, { Component, useState, useEffect  } from 'react';
import 'bootstrap/dist/css/bootstrap.css';
import { Jumbotron, Container, Card, Button, CardImg, CardTitle, CardText, CardHeader,
         CardDeck, CardColumns, CardSubtitle, CardBody , NavLink, UncontrolledCarousel,
         Form,FormGroup, Label, Input, FormText} from 'reactstrap';
import { Image, Col, Row, Pagination, Nav, Tabs, Tab, DropdownButton, Dropdown, ButtonToolbar, Table} from 'react-bootstrap';
import { BrowserRouter as Router, Route, Link , Switch, BrowserHistory } from 'react-router-dom';
import { createBrowserHistory } from 'history';
import { createUltimatePagination, ITEM_TYPES } from 'react-ultimate-pagination';
import ReactDOM from 'react-dom';
import Highlighter from 'react-highlight-words';
/*
https://stackoverflow.com/questions/35976167/find-unique-values-from-an-array-in-react-js
*/

export class Search extends React.Component {

  constructor(props){
    super(props)
    this.state = {
      searchstring : null
    };
  }

    componentDidMount() {
      const key = this.props.match.params;
    }

  render(){
      const {key} = this.props.match.params;
    return(
        <div>
        {/*Make tabs not look shitty*/}
        <Tabs>
          <Tab.Content eventKey="location" title="Location" >
            <LocationResults fieldname = "city" searchstring = {key}/>
          </Tab.Content>
          <Tab eventKey="illness" title="Illness">
            <h1 style={{color: '#1976d2'}}>Illnesses Search Results for "{key}"</h1>
            <IllnessResults fieldname = "illness_desc" searchstring = {key}/>
          </Tab>
          <Tab eventKey="pollutants" title="Pollutants">
            <h1 style={{color: '#1976d2'}}>Polluants Search Results for "{key}"</h1>
            {/*<PollutantResults fieldname = "chemical_name" searchstring = {key}/>*/}
          </Tab>
          </Tabs>
          </div>
    );
  }
}

const buildOrder  = (fieldname,direction) => {
    return `{%22field%22:%22${fieldname}%22,%22direction%22:%22${direction}%22}`
}

const buildFilter = (fieldname,operator,value) => {
    return `{%22name%22:%22${fieldname}%22,%22op%22:%22${operator}%22,%22val%22:%22${value}%22}`
}

const buildQuery = (model,page,filterObjs,orderObjs) => {
      return `https://api.howsmyair.me/${model}?page=${page}&q={%22filters%22:[${filterObjs}],%22order_by%22:[${orderObjs}]}`
}

export class LocationResults extends React.Component {

  constructor(props){
    super(props);
    this.state = {
      data : {},
      num_results: null,
      objects:[],
      page: null,
      total_pages: null,
      states : [],
      time_zones : [],
      current_state: "None",
      current_timezone : "None",
      current_population : "None",
      current_density : "None",
      sort_by : "city",
      display_sort : "City",
      order : "asc",
      display_order : "Ascending",
      curent_page: 0,
      isloaded : false,
      current_query: buildQuery("location",buildFilter("city","like",`%25${this.props.searchstring}%25`),buildOrder("city","desc"))
    };
        this.getUniqueStates = this.getUniqueStates.bind(this)
        this.getTimeZones = this.getTimeZones.bind(this)
        this.getLocationQuery = this.getLocationQuery.bind(this)
        this.componentDidMount = this.componentDidMount.bind(this)

  }

    componentDidMount() {
      console.log(this.getLocationQuery())
      fetch(this.getLocationQuery())
         .then(res => res.json())
         .then(data => {
          this.setState({
            data : data,
            num_results: data.num_results,
            objects: data.objects,
            curent_page: data.page-1,
            page: data.page-1,
            total_pages: data.total_pages,
            isloaded : true,
            states : this.getUniqueStates(data.objects),
            time_zones : this.getTimeZones(data.objects)
          })

      })
    };

    getLocationQuery(){

      let filterObjs = buildFilter("city","like",`%25${this.props.searchstring}%25`);
      let orderObjs = buildOrder("city","asc")

      if (this.state.current_state != "None") {
        filterObjs += ","+buildFilter("state_name","eq",this.state.current_state)
      }
      if (this.state.current_timezone != "None") {
        filterObjs += ","+buildFilter("timezone","eq",this.state.current_timezone)
      }

      switch(this.state.current_population){
        case "Small":
          filterObjs += ","+buildFilter("population","le",500000)
          break;
        case"Medium":
          filterObjs += ","+buildFilter("population","ge",500000)
          filterObjs += ","+buildFilter("population","lt",1000000)
          break;
        case"Large":
          filterObjs += ","+buildFilter("population","ge",1000000)
        default:
          break;

      }

      switch(this.state.current_density){
        case "Sparse":
          filterObjs += ","+buildFilter("density","le",1000)
          break;
        case"Medium":
          filterObjs += ","+buildFilter("density","ge",1000)
          filterObjs += ","+buildFilter("density","lt",2000)
          break;
        case"Dense":
          filterObjs += ","+buildFilter("density","ge",2000)
        default:
          break;
      }
      orderObjs = buildOrder(this.state.sort_by,this.state.order)

      return buildQuery("location",this.state.curent_page+1,filterObjs,orderObjs);
    }

    getUniqueStates(objArray){
        const uniqueVals = [];
        objArray.map( obj => {
            if (uniqueVals.indexOf(obj.state_name) === -1) {
                uniqueVals.push(obj.state_name)
            };
        });
        return(uniqueVals);
    }

    getTimeZones(objArray){
        const uniqueVals = [];
        objArray.map( obj => {
            if (uniqueVals.indexOf(obj.timezone) === -1) {
                uniqueVals.push(obj.timezone)
            };
        });
        return(uniqueVals);
    }

    render(){
      if (this.state.isloaded){
      console.log(this.state.curent_page)
      console.log(this.state.page)
      console.log(this.state.total_pages)
      console.log(this.state.num_results)
      let span = 2
      let page = this.state.page
      let end = this.state.total_pages - 1
      let l = page - (span / 2)
      let r = page + (span / 2)
      let inbetween = [];

      if (this.state.total_pages < span + 1){
          for (let p = 0; p < this.state.total_pages; p++) {
            inbetween.push(
              <Pagination.Item
                active={p === page}
                onClick={e => this.setState({ curent_page : p , isloaded : false})}
              >
              {p + 1}
              </Pagination.Item>
            );
          }
      } else if (page-span < 0) {
        for (let p = 0; p <= span; p++) {
          inbetween.push(
            <Pagination.Item
              active={p === page}
              onClick={e => this.setState({ curent_page : p , isloaded : false})}
            >
            {p + 1}
            </Pagination.Item>
          );
        }
      } else if (page + span > end) {
          for (let p = end - span; p <= end ; p++) {
            inbetween.push(
              <Pagination.Item
                active={p === page}
              onClick={e => this.setState({ curent_page : p , isloaded : false})}
              >
              {p + 1}
              </Pagination.Item>
          );
        }
      } else {
        for (let p = l < 0 ? 0 : l; p <= r && p <= end; p++) {
          inbetween.push(
            <Pagination.Item
              active={p === page}
              onClick={e => this.setState({ curent_page : p , isloaded : false})}
            >
            {p + 1}
            </Pagination.Item>
          );
        }
      }
      return(
        <div>
          <br/>
            <Row className="justify-content-md-center">
                <h1 style={{color: '#1976d2'}}>Location Search Results for "{this.props.searchstring}"</h1>
            </Row>
            <Row className="justify-content-md-center">
              <Col md="auto">
                <h3 style={{color: '#1976d2'}}>Filters</h3>
              </Col>
              <Col md="auto">
                <center>
                  <DropdownButton id="dropdown-basic-button" title="States">
                      <Dropdown.Item onClick={() => {this.setState({current_state : "None",isloaded : false});}}>
                        None
                      </Dropdown.Item>
                      {this.state.states.map( st => (
                        <Dropdown.Item onClick={() => {
                          this.setState({
                            current_state : st,
                            isloaded : false
                          });
                        }}>
                        {st}
                        </Dropdown.Item>
                      ))}
                  </DropdownButton>
                  <h5 style={{color: '#1976d2'}}>{this.state.current_state}</h5>
                </center>
              </Col>
              <Col md="auto">
                <center>
                  <DropdownButton id="dropdown-basic-button" title="Time Zone">
                    <Dropdown.Item onClick={() => {this.setState({current_timezone : "None",isloaded : false});}}>
                      None
                    </Dropdown.Item>
                      {this.state.time_zones.map( tz => (
                        <Dropdown.Item onClick={() => {
                            this.setState({
                              current_timezone : tz,
                              isloaded : false
                            });
                        }}>
                        {tz}
                    </Dropdown.Item>
                  ))}
                  </DropdownButton>
                  <h5 style={{color: '#1976d2'}}>{this.state.current_timezone}</h5>
                </center>
              </Col>
              <Col md="auto">
                <center>
                  <DropdownButton id="dropdown-basic-button" title="Population">
                    <Dropdown.Item onClick={() => {this.setState({ current_population : "None", isloaded : false});}}>
                      None
                    </Dropdown.Item>
                    <Dropdown.Item onClick={() => {this.setState({ current_population : "Small", isloaded : false});}}>
                      Small
                    </Dropdown.Item>
                    <Dropdown.Item onClick={() => {this.setState({ current_population : "Medium", isloaded : false});}}>
                      Medium
                    </Dropdown.Item>
                    <Dropdown.Item onClick={() => {this.setState({ current_population : "Large", isloaded : false});}}>
                      Large
                    </Dropdown.Item>
                  </DropdownButton>
                <h5 style={{color: '#1976d2'}}><center>{this.state.current_population}</center></h5>
                </center>
              </Col>
              <Col md="auto">
                <center>
                  <DropdownButton id="dropdown-basic-button" title="Density">
                    <Dropdown.Item onClick={() => {this.setState({ current_density : "None", isloaded : false});}}>
                      None
                    </Dropdown.Item>
                    <Dropdown.Item onClick={() => {this.setState({ current_density : "Sparse", isloaded : false});}}>
                      Sparse
                    </Dropdown.Item>
                    <Dropdown.Item onClick={() => {this.setState({ current_density : "Medium", isloaded : false});}}>
                      Medium
                    </Dropdown.Item>
                    <Dropdown.Item onClick={() => {this.setState({ current_density : "Dense", isloaded : false});}}>
                      Dense
                    </Dropdown.Item>
                  </DropdownButton>
                <h5 style={{color: '#1976d2'}}><center>{this.state.current_density}</center></h5>
                </center>
              </Col>
                <h3 style={{color: '#1976d2'}}><center>Sort By</center></h3>
              <Col md="auto">
                  <DropdownButton id="dropdown-basic-button" title={this.state.display_sort}>
                    <Dropdown.Item onClick={() => {this.setState({ sort_by : "city", display_sort : "City", isloaded : false});}}>
                      City
                    </Dropdown.Item>
                    <Dropdown.Item onClick={() => {this.setState({ sort_by : "county_name", display_sort : "County", isloaded : false});}}>
                      County
                    </Dropdown.Item>
                    <Dropdown.Item onClick={() => {this.setState({ sort_by : "state_name", display_sort : "State", isloaded : false});}}>
                      State
                    </Dropdown.Item>
                    <Dropdown.Item onClick={() => {this.setState({ sort_by : "timezone", display_sort : "Time Zone", isloaded : false});}}>
                      Time Zone
                    </Dropdown.Item>
                    <Dropdown.Item onClick={() => {this.setState({ sort_by : "latitude", display_sort : "Latitude", isloaded : false});}}>
                      Latitude
                    </Dropdown.Item>
                    <Dropdown.Item onClick={() => {this.setState({ sort_by : "longitude", display_sort : "Longitude", isloaded : false});}}>
                      Longitude
                    </Dropdown.Item>
                    <Dropdown.Item onClick={() => {this.setState({ sort_by : "population", display_sort : "Population", isloaded : false});}}>
                      Population
                    </Dropdown.Item>
                    <Dropdown.Item onClick={() => {this.setState({ sort_by : "density", display_sort : "Density", isloaded : false});}}>
                      Density
                    </Dropdown.Item>
                  </DropdownButton>
              </Col>
              <Col md="auto">
                  <DropdownButton id="dropdown-basic-button" title = {this.state.display_order}>
                    <Dropdown.Item onClick={() => {this.setState({ order : "asc", display_order : "Ascending", isloaded : false});}}>
                      Ascending
                    </Dropdown.Item>
                    <Dropdown.Item onClick={() => {this.setState({ order : "desc", display_order : "Descending", isloaded : false});}}>
                      Descending
                    </Dropdown.Item>
                  </DropdownButton>
              </Col>
            </Row>
          <br/>
          <Container>
            <Row className="justify-content-md-center">
              <Pagination>
                {/*First Page*/}
                <Pagination.First
                  onClick={e => this.setState({ curent_page : 0, isloaded : false})}
                />

                {/*Previous Page*/}
                <Pagination.Prev
                  disabled={this.state.page <= 0}
                  onClick={e => this.setState({ curent_page : this.state.page -1 , isloaded : false})}
                />

                {/*Eveything in between - see block before return statement*/}
                {inbetween}

                {/*Next Page*/}
                <Pagination.Next
                  disabled={this.state.page >= this.state.total_pages - 1}
                  onClick={e => this.setState({ curent_page : this.state.page +1 , isloaded : false})}
                />

                {/*Last Page*/}
                <Pagination.Last
                  onClick={e => this.setState({ curent_page : this.state.total_pages - 1 , isloaded : false})}
                />
              </Pagination>
            </Row>
           </Container>

          <Table striped bordered hover responsive >
            <th><center>City</center></th>
            <th><center>County</center></th>
            <th><center>State</center></th>
            <th><center>Time Zone</center></th>
            <th><center>Latitude</center></th>
            <th><center>Longitude</center></th>
            <th><center>Population</center></th>
            <th><center>Density per sq. mile</center></th>
            <tbody>
            {this.state.objects.map((location) => (
              <tr>
              <td><center>
                  <Link to={"/Location/" + location.id}>
                      <Highlighter
                        highlightStyle={{backgroundColor: 'lightblue'}}
                        searchWords={[this.props.searchstring]}
                        autoEscape={true}
                        textToHighlight= {location.city}
                      />
                  </Link>
                </center>
              </td>
              <td><center>{location.county_name}</center></td>
              <td><center>{location.state_name}</center></td>
              <td><center>{location.timezone}</center></td>
              <td><center>{location.latitude}</center></td>
              <td><center>{location.longitude}</center></td>
              <td><center>{location.population}</center></td>
              <td><center>{location.density}</center></td>
              </tr>
            ))}
            </tbody>
          </Table>
        </div>
      );
    } else {
      this.componentDidMount();
      return (
         <p>Loading</p>
      );
   }
  }
}

export default createUltimatePagination({
  WrapperComponent: Pagination,
  itemTypeToComponent: {
    [ITEM_TYPES.PAGE]: ({value, isActive, onClick}) => (
      <Pagination.Item active={isActive} onClick={onClick}>{value}</Pagination.Item>
    ),
    [ITEM_TYPES.ELLIPSIS]: ({ isActive, onClick }) => (
      <Pagination.Ellipsis disabled={isActive} onClick={onClick} />
    ),
    [ITEM_TYPES.FIRST_PAGE_LINK]: ({ isActive, onClick }) => (
      <Pagination.First disabled={isActive} onClick={onClick} />
    ),
    [ITEM_TYPES.PREVIOUS_PAGE_LINK]: ({ isActive, onClick }) => (
      <Pagination.Prev disabled={isActive} onClick={onClick} />
    ),
    [ITEM_TYPES.NEXT_PAGE_LINK]: ({ isActive, onClick }) => (
      <Pagination.Next disabled={isActive} onClick={onClick} />
    ),
    [ITEM_TYPES.LAST_PAGE_LINK]: ({ isActive, onClick }) => (
      <Pagination.Last disabled={isActive} onClick={onClick} />
    ),
  },
});


export class IllnessResults extends React.Component {

  constructor(props){
    super(props);
    this.state = {
      data : {},
      num_results: null,
      objects:[],
      page: null,
      total_pages: null,
      states : [],
      time_zones : [],
      current_contagious: "None",
      current_lethality : "None",
      current_treatable : "None",
      sort_by : "illness_name",
      display_sort : "Illness",
      order : "asc",
      display_order : "Ascending",
      curent_page: 0,
      isloaded : false,
      current_query: buildQuery("illness",buildFilter("illness_desc","like",`%25${this.props.searchstring}%25`),buildOrder("illness","illness_desc"))
    };
    this.getQuery = this.getQuery.bind(this)
    this.componentDidMount = this.componentDidMount.bind(this)
   }

   componentDidMount() {
   console.log(this.getQuery())
   fetch(this.getQuery())
       .then(res => res.json())
       .then(data => {
        this.setState({
         data : data,
         num_results: data.num_results,
         objects: data.objects,
         curent_page: data.page-1,
         page: data.page-1,
         total_pages: data.total_pages,
         isloaded : true,
        })

   })
   };

   getQuery(){

   let filterObjs = buildFilter("illness_name","like",`%25%25`);
   let orderObjs = buildOrder("illness_name","asc")

   if (this.state.current_contagious != "None") {
     filterObjs += ","+buildFilter("illness_contagious","eq",this.state.current_contagious)
   }
   if (this.state.current_lethality != "None") {
     filterObjs += ","+buildFilter("illness_lethality","eq",this.state.current_lethality)
   }
   if (this.state.current_treatable != "None") {
     filterObjs += ","+buildFilter("illness_treatable","eq",this.state.current_treatable)
   }

   orderObjs = buildOrder(this.state.sort_by,this.state.order)

   return buildQuery("illness",this.state.curent_page+1,filterObjs,orderObjs);
   }

   render(){
     if (this.state.isloaded) {
     console.log(this.state.curent_page)
     console.log(this.state.page)
     console.log(this.state.total_pages)
     console.log(this.state.num_results)
     let span = 4
     let page = this.state.page
     let end = this.state.total_pages - 1
     let l = page - (span / 2)
     let r = page + (span / 2)
     let inbetween = [];

     if (this.state.total_pages < span + 1){
         for (let p = 0; p < this.state.total_pages; p++) {
           inbetween.push(
             <Pagination.Item
               active={p === page}
               onClick={e => this.setState({ curent_page : p , isloaded : false})}
             >
             {p + 1}
             </Pagination.Item>
           );
         }
     } else if (page-span < 0) {
      for (let p = 0; p <= span; p++) {
         inbetween.push(
           <Pagination.Item
             active={p === page}
             onClick={e => this.setState({ curent_page : p , isloaded : false})}
           >
           {p + 1}
           </Pagination.Item>
         );
      }
     } else if (page + span > end) {
         for (let p = end - span; p <= end ; p++) {
           inbetween.push(
             <Pagination.Item
               active={p === page}
             onClick={e => this.setState({ curent_page : p , isloaded : false})}
             >
             {p + 1}
             </Pagination.Item>
         );
      }
     } else {
      for (let p = l < 0 ? 0 : l; p <= r && p <= end; p++) {
         inbetween.push(
           <Pagination.Item
             active={p === page}
             onClick={e => this.setState({ curent_page : p , isloaded : false})}
           >
           {p + 1}
           </Pagination.Item>
         );
      }
     }
     return(
      <div>
         <br/>
           <Row className="justify-content-md-center">
               <h1 style={{color: '#1976d2'}}>{this.state.num_results} Illnesses</h1>
           </Row>
           <Row className="justify-content-md-center">
             <Col md="auto">
               <h3 style={{color: '#1976d2'}}>Filters</h3>
             </Col>
             <Col md="auto">
               <center>
                 <DropdownButton id="dropdown-basic-button" title="Contagious">
                   <Dropdown.Item onClick={() => {this.setState({ current_contagious : "None", isloaded : false});}}>
                     None
                   </Dropdown.Item>
                   <Dropdown.Item onClick={() => {this.setState({ current_contagious : "yes", isloaded : false});}}>
                     Yes
                   </Dropdown.Item>
                   <Dropdown.Item onClick={() => {this.setState({ current_contagious : "no", isloaded : false});}}>
                     No
                   </Dropdown.Item>
                 </DropdownButton>
                 <h5 style={{color: '#1976d2'}}>{this.state.current_contagious}</h5>
               </center>
             </Col>
             <Col md="auto">
               <center>
                 <DropdownButton id="dropdown-basic-button" title="Treatable">
                   <Dropdown.Item onClick={() => {this.setState({ current_lethality : "None", isloaded : false});}}>
                     None
                   </Dropdown.Item>
                   <Dropdown.Item onClick={() => {this.setState({ current_lethality : "lethal", isloaded : false});}}>
                     Yes
                   </Dropdown.Item>
                   <Dropdown.Item onClick={() => {this.setState({ current_lethality : "non-lethal", isloaded : false});}}>
                     No
                   </Dropdown.Item>
                 </DropdownButton>
                 <h5 style={{color: '#1976d2'}}>{this.state.current_contagious}</h5>
               </center>
             </Col>
             <Col md="auto">
               <center>
                 <DropdownButton id="dropdown-basic-button" title="Treatable">
                   <Dropdown.Item onClick={() => {this.setState({ current_treatable : "None", isloaded : false});}}>
                     None
                   </Dropdown.Item>
                   <Dropdown.Item onClick={() => {this.setState({ current_treatable : "yes", isloaded : false});}}>
                     Yes
                   </Dropdown.Item>
                   <Dropdown.Item onClick={() => {this.setState({ current_treatable : "no", isloaded : false});}}>
                     No
                   </Dropdown.Item>
                 </DropdownButton>
                 <h5 style={{color: '#1976d2'}}>{this.state.current_treatable}</h5>
               </center>
             </Col>
             <h3 style={{color: '#1976d2'}}><center>Sort By</center></h3>
             <Col md="auto">
                 <DropdownButton id="dropdown-basic-button" title={this.state.display_sort}>
                   <Dropdown.Item onClick={() => {this.setState({ sort_by : "illness_name", display_sort : "Illness", isloaded : false});}}>
                     Illness
                   </Dropdown.Item>
                   <Dropdown.Item onClick={() => {this.setState({ sort_by : "illness_lethality", display_sort : "Lethality", isloaded : false});}}>
                     Lethality
                   </Dropdown.Item>
                   <Dropdown.Item onClick={() => {this.setState({ sort_by : "illness_contagious", display_sort : "Contagious", isloaded : false});}}>
                     Contagious
                   </Dropdown.Item>
                   <Dropdown.Item onClick={() => {this.setState({ sort_by : "illness_treatable", display_sort : "Treatable", isloaded : false});}}>
                     Treatable
                   </Dropdown.Item>
                 </DropdownButton>
             </Col>
             <Col md="auto">
                 <DropdownButton id="dropdown-basic-button" title = {this.state.display_order}>
                   <Dropdown.Item onClick={() => {this.setState({ order : "asc", display_order : "Ascending", isloaded : false});}}>
                     Ascending
                   </Dropdown.Item>
                   <Dropdown.Item onClick={() => {this.setState({ order : "desc", display_order : "Descending", isloaded : false});}}>
                     Descending
                   </Dropdown.Item>
                 </DropdownButton>
             </Col>
           </Row>
         <br/>
         <Container>
           <center><h4 style={{color: '#1976d2'}}>Page {this.state.curent_page+1}/{this.state.total_pages}</h4></center>
           <Row className="justify-content-md-center">
             <Pagination>
               {/*First Page*/}
               <Pagination.First
                 onClick={e => this.setState({ curent_page : 0, isloaded : false})}
               />

               {/*Previous Page*/}
               <Pagination.Prev
                 disabled={this.state.page <= 0}
                 onClick={e => this.setState({ curent_page : this.state.page -1 , isloaded : false})}
               />

               {/*Eveything in between - see block before return statement*/}
               {inbetween}

               {/*Next Page*/}
               <Pagination.Next
                 disabled={this.state.page >= this.state.total_pages - 1}
                 onClick={e => this.setState({ curent_page : this.state.page +1 , isloaded : false})}
               />

               {/*Last Page*/}
               <Pagination.Last
                 onClick={e => this.setState({ curent_page : this.state.total_pages - 1 , isloaded : false})}
               />
             </Pagination>
           </Row>
          </Container>

          <Table striped bordered hover responsive >
            <th><center>Illness</center></th>
            <th><center>Illness Description</center></th>
            <th><center>Lethality</center></th>
            <th><center>Symptoms</center></th>
            <th><center>Contagious</center></th>
            <th><center>Treatable</center></th>
            <tbody>
            {this.state.objects.map((illness) => (
              <tr>
              <td><center>{illness.illness_name}</center></td>
              <td><center>
                  <Link to={"/Location/" + illness.illness_name}>
                      <Highlighter
                        highlightStyle={{backgroundColor: 'lightblue'}}
                        searchWords={[this.props.searchstring]}
                        autoEscape={true}
                        textToHighlight= {illness.illness_desc}
                      />
                  </Link>
                </center>
              </td>
              <td><center>{illness.illness_lethality}</center></td>
              <td><center>{illness.illness_symptoms}</center></td>
              <td><center>{illness.illness_contagious}</center></td>
              <td><center>{illness.illness_treatable}</center></td>
              </tr>
            ))}
            </tbody>
          </Table>
        </div>
      );
    } else {
      this.componentDidMount();
      return (
        <div>
         <center>omae wa mou shindeiru</center>
        </div>

      );
   }
  }
}
