import React, { Component } from 'react';
import 'bootstrap/dist/css/bootstrap.css';
import { Jumbotron, Container, Card, Button, CardImg, CardTitle, CardText, CardHeader,
         CardDeck, CardColumns, CardSubtitle, CardBody , NavLink, Carousel,
         Form,FormGroup, Label, Input, FormText } from 'reactstrap';
import { Image, Col, Row, Pagination, DropdownButton, Dropdown, ButtonToolbar } from 'react-bootstrap';
import { BrowserRouter as Router, Route, Link , Switch, BrowserHistory } from 'react-router-dom';
import { createBrowserHistory } from 'history';
import { createUltimatePagination, ITEM_TYPES } from 'react-ultimate-pagination';
import ReactDOM from 'react-dom';
import Illnesses from '../illnesses/Illnesses';
import Pollutants from '../pollutants/Pollutants';
import PollutantInstance from '../pollutants/PollutantInstance';
import IllnessInstance from '../illnesses/IllnessInstance';

/**
Sources:

Pagination sources:
https://github.com/react-bootstrap/pagination
https://www.npmjs.com/package/react-ultimate-pagination
https://codepen.io/joedazza/pen/BVXVej
**/

const ENDPOINT = 'https://api.howsmyair.me/location';

export class Location extends Component {

   constructor(props) {
      super(props);
      this.state = {
        data: {},
        location_url : null,
        related_illnesses: [],
        related_pollutants: []
      };
   }

   componentDidMount() {

      const {key} = this.props.match.params;

      // get location
      fetch(ENDPOINT + '/' + key)
         .then(res => res.json())
         .then(data => { this.setState({
            data: data,
            location_url : "https://maps.googleapis.com/maps/api/staticmap?center="+
                            data.latitude+","+data.longitude+
                            "&zoom=12&size=400x400"+
                            "&markers=color:green%7Clabel:G%7C"+
                            data.latitude+","+data.longitude +
                            "&key=AIzaSyB-U0Z7cqufI-MHmRwfLmgqDCyQmOlIq48",
            related_illnesses : data.related_illnesses,
            related_pollutants : data.related_pollutants
         })
      })
   };

   render() {
      return (
        <div>
            <Container>
            <p>
            <br/>
            <h1>{this.state.data.city}</h1>
            <h2 class = "card-subtitle mb-2 text-muted">{this.state.data.state_name}</h2>
            <br/>
            County: {this.state.data.county_name}
            <br/>Latitude: {this.state.data.latitude}
            <br/>Longitude: {this.state.data.longitude}
            <br/>Population: {this.state.data.population}
            <br/>Density (per sq. mile): {this.state.data.density}
            <br/>Time Zone: {this.state.data.timezone}
            </p>
            <img src={this.state.location_url} alt="Italian Trulli"/>
            </Container>

            {/*related illnessess*/}
            <Container>
            <h6> Related Illnesses </h6>
            <Jumbotron>
            <Row className="justify-content-md-center" className="spacing">
            {this.state.related_illnesses.map((illness) => (
               <Col className="spacing" lg="4" sm="6" xs="12">
                  <IllnessInstance illness_name={illness.illness_name} />
               </Col>
            ))}
            </Row>
            </Jumbotron>
            </Container>

            {/*related pollutants*/}
            <Container>
            <h6>Related Polluants</h6>
            <Jumbotron>
            <Row className="justify-content-md-center" className="spacing">
            {this.state.related_pollutants.map((pollutant) => (
               <Col className="spacing" lg="4" sm="6" xs="12">
                <PollutantInstance pollutant_name={pollutant.pollutant_name} />
                <p />
             </Col>
            ))}
            </Row>
            </Jumbotron>
            </Container>
        </div>
      );
   }
}


const buildOrder  = (fieldname,direction) => {
    return `{%22field%22:%22${fieldname}%22,%22direction%22:%22${direction}%22}`
}

const buildFilter = (fieldname,operator,value) => {
    return `{%22name%22:%22${fieldname}%22,%22op%22:%22${operator}%22,%22val%22:%22${value}%22}`
}

const buildQuery = (model,page,filterObjs,orderObjs) => {
      return `https://api.howsmyair.me/${model}?page=${page}&q={%22filters%22:[${filterObjs}],%22order_by%22:[${orderObjs}]}`
}


export class Locations extends Component {

  constructor(props){
    super(props);
    this.state = {
      data : {},
      num_results: null,
      objects:[],
      page: null,
      total_pages: null,
      states : [],
      time_zones : [],
      current_state: "None",
      current_timezone : "None",
      current_population : "None",
      current_density : "None",
      sort_by : "city",
      display_sort : "City",
      order : "asc",
      display_order : "Ascending",
      curent_page: 0,
      isloaded : false,
      current_query: buildQuery("location",buildFilter("city","like",`%25${this.props.searchstring}%25`),buildOrder("city","desc"))
    };
        this.getUniqueStates = this.getUniqueStates.bind(this)
        this.getTimeZones = this.getTimeZones.bind(this)
        this.getLocationQuery = this.getLocationQuery.bind(this)
        this.componentDidMount = this.componentDidMount.bind(this)

  }

    componentDidMount() {
      console.log(this.getLocationQuery())
      fetch(this.getLocationQuery())
         .then(res => res.json())
         .then(data => {
          this.setState({
            data : data,
            num_results: data.num_results,
            objects: data.objects,
            curent_page: data.page-1,
            page: data.page-1,
            total_pages: data.total_pages,
            isloaded : true,
            states : this.getUniqueStates(data.objects),
            time_zones : this.getTimeZones(data.objects)
          })

      })
    };

    getLocationQuery(){

      let filterObjs = buildFilter("city","like",`%25%25`);
      let orderObjs = buildOrder("city","asc")

      if (this.state.current_state != "None") {
        filterObjs += ","+buildFilter("state_name","eq",this.state.current_state)
      }
      if (this.state.current_timezone != "None") {
        filterObjs += ","+buildFilter("timezone","eq",this.state.current_timezone)
      }

      switch(this.state.current_population){
        case "Small":
          filterObjs += ","+buildFilter("population","le",500000)
          break;
        case"Medium":
          filterObjs += ","+buildFilter("population","ge",500000)
          filterObjs += ","+buildFilter("population","lt",1000000)
          break;
        case"Large":
          filterObjs += ","+buildFilter("population","ge",1000000)
        default:
          break;

      }

      switch(this.state.current_density){
        case "Sparse":
          filterObjs += ","+buildFilter("density","le",1000)
          break;
        case"Medium":
          filterObjs += ","+buildFilter("density","ge",1000)
          filterObjs += ","+buildFilter("density","lt",2000)
          break;
        case"Dense":
          filterObjs += ","+buildFilter("density","ge",2000)
        default:
          break;
      }
      orderObjs = buildOrder(this.state.sort_by,this.state.order)

      return buildQuery("location",this.state.curent_page+1,filterObjs,orderObjs);
    }

    getUniqueStates(objArray){
        const uniqueVals = [];
        objArray.map( obj => {
            if (uniqueVals.indexOf(obj.state_name) === -1) {
                uniqueVals.push(obj.state_name)
            };
        });
        return(uniqueVals);
    }

    getTimeZones(objArray){
        const uniqueVals = [];
        objArray.map( obj => {
            if (uniqueVals.indexOf(obj.timezone) === -1) {
                uniqueVals.push(obj.timezone)
            };
        });
        return(uniqueVals);
    }

    render(){
      if (this.state.isloaded){
      console.log(this.state.curent_page)
      console.log(this.state.page)
      console.log(this.state.total_pages)
      console.log(this.state.num_results)
      let span = 4
      let page = this.state.page
      let end = this.state.total_pages - 1
      let l = page - (span / 2)
      let r = page + (span / 2)
      let inbetween = [];

      if (this.state.total_pages < span + 1){
          for (let p = 0; p < this.state.total_pages; p++) {
            inbetween.push(
              <Pagination.Item
                active={p === page}
                onClick={e => this.setState({ curent_page : p , isloaded : false})}
              >
              {p + 1}
              </Pagination.Item>
            );
          }
      } else if (page-span < 0) {
        for (let p = 0; p <= span; p++) {
          inbetween.push(
            <Pagination.Item
              active={p === page}
              onClick={e => this.setState({ curent_page : p , isloaded : false})}
            >
            {p + 1}
            </Pagination.Item>
          );
        }
      } else if (page + span > end) {
          for (let p = end - span; p <= end ; p++) {
            inbetween.push(
              <Pagination.Item
                active={p === page}
              onClick={e => this.setState({ curent_page : p , isloaded : false})}
              >
              {p + 1}
              </Pagination.Item>
          );
        }
      } else {
        for (let p = l < 0 ? 0 : l; p <= r && p <= end; p++) {
          inbetween.push(
            <Pagination.Item
              active={p === page}
              onClick={e => this.setState({ curent_page : p , isloaded : false})}
            >
            {p + 1}
            </Pagination.Item>
          );
        }
      }
      return(
        <div>
          <br/>
            <Row className="justify-content-md-center">
                <h1 style={{color: '#1976d2'}}>{this.state.num_results} Locations</h1>
            </Row>
            <Row className="justify-content-md-center">
              <Col md="auto">
                <h3 style={{color: '#1976d2'}}>Filters</h3>
              </Col>
              <Col md="auto">
                <center>
                  <DropdownButton id="dropdown-basic-button" title="States">
                      <Dropdown.Item onClick={() => {this.setState({current_state : "None", isloaded : false});}}>
                        None
                      </Dropdown.Item>
                      {this.state.states.map( st => (
                        <Dropdown.Item onClick={() => {
                          this.setState({
                            current_state : st,
                            isloaded : false
                          });
                        }}>
                        {st}
                        </Dropdown.Item>
                      ))}
                  </DropdownButton>
                  <h5 style={{color: '#1976d2'}}>{this.state.current_state}</h5>
                </center>
              </Col>
              <Col md="auto">
                <center>
                  <DropdownButton id="dropdown-basic-button" title="Time Zone">
                    <Dropdown.Item onClick={() => {this.setState({current_timezone : "None",isloaded : false});}}>
                      None
                    </Dropdown.Item>
                      {this.state.time_zones.map( tz => (
                        <Dropdown.Item onClick={() => {
                            this.setState({
                              current_timezone : tz,
                              isloaded : false
                            });
                        }}>
                        {tz}
                    </Dropdown.Item>
                  ))}
                  </DropdownButton>
                  <h5 style={{color: '#1976d2'}}>{this.state.current_timezone}</h5>
                </center>
              </Col>
              <Col md="auto">
                <center>
                  <DropdownButton id="dropdown-basic-button" title="Population">
                    <Dropdown.Item onClick={() => {this.setState({ current_population : "None", isloaded : false});}}>
                      None
                    </Dropdown.Item>
                    <Dropdown.Item onClick={() => {this.setState({ current_population : "Small", isloaded : false});}}>
                      Small
                    </Dropdown.Item>
                    <Dropdown.Item onClick={() => {this.setState({ current_population : "Medium", isloaded : false});}}>
                      Medium
                    </Dropdown.Item>
                    <Dropdown.Item onClick={() => {this.setState({ current_population : "Large", isloaded : false});}}>
                      Large
                    </Dropdown.Item>
                  </DropdownButton>
                <h5 style={{color: '#1976d2'}}><center>{this.state.current_population}</center></h5>
                </center>
              </Col>
              <Col md="auto">
                <center>
                  <DropdownButton id="dropdown-basic-button" title="Density">
                    <Dropdown.Item onClick={() => {this.setState({ current_density : "None", isloaded : false});}}>
                      None
                    </Dropdown.Item>
                    <Dropdown.Item onClick={() => {this.setState({ current_density : "Sparse", isloaded : false});}}>
                      Sparse
                    </Dropdown.Item>
                    <Dropdown.Item onClick={() => {this.setState({ current_density : "Medium", isloaded : false});}}>
                      Medium
                    </Dropdown.Item>
                    <Dropdown.Item onClick={() => {this.setState({ current_density : "Dense", isloaded : false});}}>
                      Dense
                    </Dropdown.Item>
                  </DropdownButton>
                <h5 style={{color: '#1976d2'}}><center>{this.state.current_density}</center></h5>
                </center>
              </Col>
                <h3 style={{color: '#1976d2'}}><center>Sort By</center></h3>
              <Col md="auto">
                  <DropdownButton id="dropdown-basic-button" title={this.state.display_sort}>
                    <Dropdown.Item onClick={() => {this.setState({ sort_by : "city", display_sort : "City", isloaded : false});}}>
                      City
                    </Dropdown.Item>
                    <Dropdown.Item onClick={() => {this.setState({ sort_by : "county_name", display_sort : "County", isloaded : false});}}>
                      County
                    </Dropdown.Item>
                    <Dropdown.Item onClick={() => {this.setState({ sort_by : "state_name", display_sort : "State", isloaded : false});}}>
                      State
                    </Dropdown.Item>
                    <Dropdown.Item onClick={() => {this.setState({ sort_by : "timezone", display_sort : "Time Zone", isloaded : false});}}>
                      Time Zone
                    </Dropdown.Item>
                    <Dropdown.Item onClick={() => {this.setState({ sort_by : "latitude", display_sort : "Latitude", isloaded : false});}}>
                      Latitude
                    </Dropdown.Item>
                    <Dropdown.Item onClick={() => {this.setState({ sort_by : "longitude", display_sort : "Longitude", isloaded : false});}}>
                      Longitude
                    </Dropdown.Item>
                    <Dropdown.Item onClick={() => {this.setState({ sort_by : "population", display_sort : "Population", isloaded : false});}}>
                      Population
                    </Dropdown.Item>
                    <Dropdown.Item onClick={() => {this.setState({ sort_by : "density", display_sort : "Density", isloaded : false});}}>
                      Density
                    </Dropdown.Item>
                  </DropdownButton>
              </Col>
              <Col md="auto">
                  <DropdownButton id="dropdown-basic-button" title = {this.state.display_order}>
                    <Dropdown.Item onClick={() => {this.setState({ order : "asc", display_order : "Ascending", isloaded : false});}}>
                      Ascending
                    </Dropdown.Item>
                    <Dropdown.Item onClick={() => {this.setState({ order : "desc", display_order : "Descending", isloaded : false});}}>
                      Descending
                    </Dropdown.Item>
                  </DropdownButton>
              </Col>
            </Row>
          <br/>
          <Container>
            <center><h4 style={{color: '#1976d2'}}>Page {this.state.curent_page+1}/{this.state.total_pages}</h4></center>
            <Row className="justify-content-md-center">
              <Pagination>
                {/*First Page*/}
                <Pagination.First
                  onClick={e => this.setState({ curent_page : 0, isloaded : false})}
                />

                {/*Previous Page*/}
                <Pagination.Prev
                  disabled={this.state.page <= 0}
                  onClick={e => this.setState({ curent_page : this.state.page -1 , isloaded : false})}
                />

                {/*Eveything in between - see block before return statement*/}
                {inbetween}

                {/*Next Page*/}
                <Pagination.Next
                  disabled={this.state.page >= this.state.total_pages - 1}
                  onClick={e => this.setState({ curent_page : this.state.page +1 , isloaded : false})}
                />

                {/*Last Page*/}
                <Pagination.Last
                  onClick={e => this.setState({ curent_page : this.state.total_pages - 1 , isloaded : false})}
                />
              </Pagination>
            </Row>
           </Container>
           <Container>
             {/*Record Grid*/}
             <Row className="justify-content-md-center" className="spacing">
             {this.state.objects.map((location) => (
                <Col className="spacing" lg="4" sm="6" xs="12">
                 <a style={{ cursor: 'pointer' }} onClick={"/Location/" + location.id} href={"/Location/" + location.id}>
                <Card body style={{ backgroundColor: '#00', borderColor: '#2255', height: '40rem' }}>
                <CardImg src={
                   "https://maps.googleapis.com/maps/api/staticmap?center="+
                                   location.latitude+","+location.longitude+
                                   "&zoom=12&size=400x400"+
                                   "&markers=color:green%7Clabel:G%7C"+
                                   location.latitude+","+location.longitude +
                                   "&key=AIzaSyB-U0Z7cqufI-MHmRwfLmgqDCyQmOlIq48"}

                                   alt="Italian Trulli" />
                  <CardHeader>
                    {/* The dynamic link is arranged this way through the NavBar.js
                    and back to the Location component*/}
                    <h5><Link to={"/Location/" + location.id}>{location.city}</Link></h5>
                    <h6 class = "card-subtitle mb-2 text-muted">{location.state_name}</h6>
                  </CardHeader>
                  <CardBody>
                  <CardText>
                    <p>
                    County: {location.county_name}
                    <br/>Latitude: {location.latitude}
                    <br/>Longitude: {location.longitude}
                    <br/>Population: {location.population}
                    <br/>Density (per sq. mile): {location.density}
                    <br/>Time Zone: {location.timezone}
                    </p>
                  </CardText>
                  </CardBody>

               </Card>
               </a>
                <p />
                </Col>
             ))}
             </Row>
         </Container>
        </div>
      );
    } else {
      this.componentDidMount();
      return (
        <div>
         <center>omae wa mou shindeiru</center>
        </div>
      );
   }
  }
}

