import React, { Component } from 'react';
import 'bootstrap/dist/css/bootstrap.css';
import { Jumbotron, Container, Card, Button, CardImg, CardTitle, CardText, CardHeader,
         CardDeck, CardColumns, CardSubtitle, CardBody , NavLink, UncontrolledCarousel,
         Form,FormGroup, Label, Input, FormText} from 'reactstrap';
import { Image, Col, Row, Pagination, DropdownButton, Dropdown, ButtonToolbar, ListGroup } from 'react-bootstrap';
import { BrowserRouter as Router, Route, Link , Switch,BrowserHistory } from 'react-router-dom';
import { createBrowserHistory } from 'history';
import ReactDOM from 'react-dom';
import Illnesses from '../illnesses/Illnesses';
import IllnessInstance,{RelatedIllness} from '../illnesses/IllnessInstance';
import { Locations, Location } from '../locations/Locations';
import LocationInstance,{RelatedLocations} from '../locations/LocationInstance';
const fetch = require("node-fetch");

/**
Sources:

Pagination sources:
https://github.com/react-bootstrap/pagination
https://www.npmjs.com/package/react-ultimate-pagination
https://codepen.io/joedazza/pen/BVXVej
**/

const ENDPOINT = 'https://api.howsmyair.me/pollutants';

export class Pollutant extends Component {

    constructor(props) {
      super(props);
      this.state = {
        data: {},
        isloaded : false,
        related_illnesses: [],
        related_locations: []
      };
    }

   componentDidMount() {
      const {key} = this.props.match.params;
      // get location
      fetch(ENDPOINT + '/' + key)
         .then(res => res.json())
         .then(data => { this.setState({
            data: data,
            isloaded : true,
            related_illnesses : data.related_illnesses,
            related_locations : data.related_locations
         })
      })
    };

   render() {
    if(this.state.isloaded){

      return (
        <div>
          <Jumbotron>
            <Container>
            <h1 style={{color: '#1976d2'}}>{this.state.data.chemical_name}</h1>
            <br/>
            <Row>
              <Col>
              <ListGroup variant="flush" >
                <ListGroup.Item  style={{color: '#1976d2'}}>
                  Cancer Potency:
                  {this.state.data.inhalation_cancer_potency} {this.state.data.inhalation_cancer_potency_units}
                  <br/><i>Source: {this.state.data.inhalation_cancer_potency_source}</i>
                </ListGroup.Item>
                <ListGroup.Item style={{color: '#1976d2'}}>
                  Reference Concentration:
                  {this.state.data.inhalation_reference_concentration} {this.state.data.inhalation_reference_concentration_units}
                  <br/><i>Source: {this.state.data.inhalation_reference_concentration_source}</i>
                </ListGroup.Item>
                <ListGroup.Item style={{color: '#1976d2'}}>
                  National Ambient Air Quality:
                  {this.state.data.national_ambient_air_quality_std} {this.state.data.national_ambient_air_quality_std_units}
                  <br/><i>Source: {this.state.data.national_ambient_air_quality_std_source}</i>
                </ListGroup.Item>
              </ListGroup>
              </Col>
              <Col>
                <Card>
                  <Row className="justify-content-md-center" className="spacing">
                    <img width="50%" src={this.state.data.chemspider_id} alt="Italian Trulli"/>
                  </Row>
                </Card>
              </Col>
            </Row>
            </Container>
          </Jumbotron>
          <Container>
            <Row>
              <Col>
                <h1 style={{color: '#1976d2'}}>Related Illnesses</h1><br/>
                <Row className="justify-content-md-center" className="spacing">
                  {this.state.related_illnesses.map((illness) => (
                     <Col className="spacing" lg="4" sm="6" xs="12">
                        <RelatedIllness illness_name={illness.illness_name} />
                        <p/>
                     </Col>
                  ))}
                </Row>
              </Col>
            </Row>
          </Container>
          <Container>
            <Row>
              <Col>
                <h1 style={{color: '#1976d2'}}>Related Locations</h1><br/>
                <Row className="justify-content-md-center" className="spacing">
                  {this.state.related_locations.map((location) => (
                     <Col className="spacing" lg="4" sm="6" xs="12">
                        <RelatedLocations location_id={location.location_id} />
                        <p/>
                     </Col>
                  ))}
                </Row>
              </Col>
            </Row>
          </Container>
        </div>
      );
    } else {
      this.componentDidMount();
      return(
        <div>
         <center><img src = "https://i.makeagif.com/media/9-30-2017/bhAyTs.gif"/></center>
        </div>
      )
    }
  }
}

const buildOrder  = (fieldname,direction) => {
    return `{%22field%22:%22${fieldname}%22,%22direction%22:%22${direction}%22}`
}

const buildFilter = (fieldname,operator,value) => {
    return `{%22name%22:%22${fieldname}%22,%22op%22:%22${operator}%22,%22val%22:%22${value}%22}`
}

const buildQuery = (model,page,filterObjs,orderObjs) => {
      return `https://api.howsmyair.me/${model}?page=${page}&q={%22filters%22:[${filterObjs}],%22order_by%22:[${orderObjs}]}`
}

export class Pollutants extends Component {

  constructor(props){
    super(props);
    this.state = {
      data : {},
      num_results: null,
      objects:[],
      page: null,
      total_pages: null,
      states : [],
      time_zones : [],
      inhalation_cancer_potency : "Unknown",
      inhalation_cancer_potency_source : "Unknown",
      inhalation_cancer_potency_units : "Unknown",
      inhalation_reference_concentration : "Unknown",
      inhalation_reference_concentration_source : "Unknown",
      inhalation_reference_concentration_units : "Unknown",
      national_ambient_air_quality_std : "Unknown",
      national_ambient_air_quality_std_source : "Unknown",
      national_ambient_air_quality_std_units : "Unknown",
      cancer_potency_sources : [],
      cancer_potency_units : [],
      reference_concentration_source : [],
      reference_concentration_units : [],
      ambient_air_quality_std_source : [],
      ambient_air_quality_std_units : [],
      sort_by : "chemical_name",
      display_sort : "Chemical Name",
      order : "asc",
      display_order : "Ascending",
      curent_page: 0,
      isloaded : false,
    };
        this.getQuery = this.getQuery.bind(this)
        this.componentDidMount = this.componentDidMount.bind(this)
  }

    componentDidMount() {
      let x = this.curent_page
      console.log(this.getQuery())
      fetch(this.getQuery())
         .then(res => res.json())
         .then(data => {
          this.setState({
            data : data,
            num_results: data.num_results,
            objects: data.objects,
            curent_page: data.page-1,
            page: data.page-1,
            total_pages: data.total_pages,
            cancer_potency_sources : this.getcancer_potency_source(data.objects),
            cancer_potency_units : this.getcancer_potency_units(data.objects),
            reference_concentration_source : this.getreference_concentration_source(data.objects),
            reference_concentration_units : this.getreference_concentration_units(data.objects),
            ambient_air_quality_std_source : this.getair_quality_std_source(data.objects),
            ambient_air_quality_std_units : this.getair_quality_std_units(data.objects),
            isloaded : true,
          })

      })
      console.log(x)
    };

    getcancer_potency_source(objArray){
        const uniqueVals = [];
        objArray.map( obj => {
            if (uniqueVals.indexOf(obj.inhalation_cancer_potency_source) === -1) {
                uniqueVals.push(obj.inhalation_cancer_potency_source)
            };
        });
        return(uniqueVals);
    }

    getcancer_potency_units(objArray){
        const uniqueVals = [];
        objArray.map( obj => {
            if (uniqueVals.indexOf(obj.inhalation_cancer_potency_units) === -1) {
                uniqueVals.push(obj.inhalation_cancer_potency_units)
            };
        });
        return(uniqueVals);
    }

    getreference_concentration_source(objArray){
        const uniqueVals = [];
        objArray.map( obj => {
            if (uniqueVals.indexOf(obj.inhalation_reference_concentration_source) === -1) {
                uniqueVals.push(obj.inhalation_reference_concentration_source)
            };
        });
        return(uniqueVals);
    }

    getreference_concentration_units(objArray){
        const uniqueVals = [];
        objArray.map( obj => {
            if (uniqueVals.indexOf(obj.inhalation_reference_concentration_units) === -1) {
                uniqueVals.push(obj.inhalation_reference_concentration_units)
            };
        });
        return(uniqueVals);
    }

    getair_quality_std_source(objArray){
        const uniqueVals = [];
        objArray.map( obj => {
            if (uniqueVals.indexOf(obj.national_ambient_air_quality_std_source) === -1) {
                uniqueVals.push(obj.national_ambient_air_quality_std_source)
            };
        });
        return(uniqueVals);
    }

    getair_quality_std_units(objArray){
        const uniqueVals = [];
        objArray.map( obj => {
            if (uniqueVals.indexOf(obj.national_ambient_air_quality_std_units) === -1) {
                uniqueVals.push(obj.national_ambient_air_quality_std_units)
            };
        });
        return(uniqueVals);
    }

