import React, { Component } from 'react';
import 'bootstrap/dist/css/bootstrap.css';
import { Jumbotron, Container, Card, Button, CardImg, CardTitle, CardText, CardHeader,
         CardDeck, CardColumns, CardSubtitle, CardBody , NavLink, UncontrolledCarousel,
         Form,FormGroup, Label, Input, FormText} from 'reactstrap';
import { Image, Col, Row, Pagination} from 'react-bootstrap';
import { BrowserRouter as Router, Route, Link , Switch,BrowserHistory } from 'react-router-dom';
import { createBrowserHistory } from 'history';
import { createUltimatePagination, ITEM_TYPES } from 'react-ultimate-pagination';
import ReactDOM from 'react-dom';
import Illnesses from '../illnesses/Illnesses';
import IllnessInstance from '../illnesses/IllnessInstance';
import { Locations, Location } from '../locations/Locations';
import LocationInstance from '../locations/LocationInstance';

/**
Sources:

Pagination sources:
https://github.com/react-bootstrap/pagination
https://www.npmjs.com/package/react-ultimate-pagination
https://codepen.io/joedazza/pen/BVXVej
**/

const ENDPOINT = 'https://api.howsmyair.me/pollutants';

export class Pollutant extends Component {

    constructor(props) {
      super(props);
      this.state = {
        data: {},
        related_illnesses: [],
        related_locations: []
      };
    }

   componentDidMount() {
      const {key} = this.props.match.params;
      // get location
      fetch(ENDPOINT + '/' + key)
         .then(res => res.json())
         .then(data => { this.setState({
            data: data,
            related_illnesses : data.related_illnesses,
            related_locations : data.related_locations
         })
      })
    };

   render() {
      return (
        <div>
            <Container>
            <p>
            <br/>
            <h1>{this.state.data.chemical_name}</h1>
            <br/>
            <b>Inhalation Cancer Potency</b>
            <br/>Potency: {this.state.data.inhalation_cancer_potency}
            <br/>Units: {this.state.data.inhalation_cancer_potency_units}
            <br/>Source: {this.state.data.inhalation_cancer_potency_source}
            <br/><b>Inhalation Reference Concentration</b>
            <br/>Concentration: {this.state.data.inhalation_reference_concentration}
            <br/>Units: {this.state.data.inhalation_reference_concentration_units}
            <br/>Source: {this.state.data.inhalation_reference_concentration_source}
            <br/><b>National Ambient Air Quality Standards</b>
            <br/>Standard: {this.state.data.inhalation_reference_concentration}
            <br/>Units: {this.state.data.inhalation_reference_concentration_units}
            <br/>Source: {this.state.data.inhalation_reference_concentration_source}
            <img src = {this.state.data.chemspider_id}/>
            </p>
            </Container>
            {/*related locations*/}
            <Container>
            <h6>Related Illnesses</h6>
            <Jumbotron>
            {this.state.related_illnesses.map((illness) => (
              <CardColumns>
                  <IllnessInstance illness_name={illness.illness_name} />
              </CardColumns>
            ))}
            </Jumbotron>
            </Container>
            {/*related pollutants*/}
            <Container>
            <h6>Related Locations</h6>
            <Jumbotron>

            {this.state.related_locations.map((location) => (
              <CardColumns>
                  <LocationInstance location_id={location.location_id} />
              </CardColumns>
            ))}
            </Jumbotron>
            </Container>
        </div>
      );
   }
}

export class Pollutants extends Component {

   /*This right here is like object attributes*/
  constructor(props) {
    super(props);
    this.state={
      num_results: null,
      objects: [],
      page: null,
      total_pages: null,
      req : null
    }
  }

  /*When the component is loaded, here's init*/
  componentDidMount() {
    fetch(ENDPOINT)
       .then(res => res.json())
       .then(data => { this.setState({
          num_results: data.num_results,
          objects: data.objects,
          page: data.page-1,
          total_pages: data.total_pages
       })
    })
  }


  /*here's how we are going to handle clicks for pagination
    Really only two things about the current state changes:
    the current page numbers, and the results shown
  */
    handleClick(e, index) {
      e.preventDefault();
      fetch(ENDPOINT + "?page="+(index+1))
         .then(res => res.json())
         .then(data => { this.setState({
            num_results: data.num_results,
            objects: data.objects,
            page: data.page-1,
            total_pages: data.total_pages
         })
      })
    }


   /*Here's how the Locations Page is rendered*/

   /*source: looking at example of pagination for Global Antidote
     https://gitlab.com/stubbsnc/globalantidote/blob/production/dynamic-site/src/pages*/

   render() {
      let span = 4
      let page = this.state.page
      let end = this.state.total_pages-1
      let l = page - (span/2)
      let r = page + (span/2)
      let inbetween = [];

      if(page-span < 0){
        for(let p = 0; p <= span; p++){
          inbetween.push(
            <Pagination.Item
              active={p === page}
              onClick={e => this.handleClick(e,p)}
            >
            {p+1}
            </Pagination.Item>
          );
        }
      } else if (page+span > end) {
          for(let p = end - span; p <= end ; p++){
            inbetween.push(
              <Pagination.Item
                active={p === page}
                onClick={e => this.handleClick(e,p)}
              >
              {p+1}
              </Pagination.Item>
          );
        }
      } else {
        for(let p = l < 0 ? 0 : l; p <= r && p <= end; p++){
          inbetween.push(
            <Pagination.Item
              active={p === page}
              onClick={e => this.handleClick(e,p)}
            >
            {p+1}
            </Pagination.Item>
          );
        }
      }

      return (
      <div>
            {/*Request Stats*/}
            <center>
               <h1>Hazardous Air Pollutants</h1>
               {this.state.num_results} Results,
               Page {this.state.page + 1} / {this.state.total_pages}
            </center>

            <Container>
              <Row className="justify-content-md-center">
                <Pagination>
                  {/*First Page*/}
                  <Pagination.First
                    onClick={e => this.handleClick(e,0)}
                  />

                  {/*Previous Page*/}
                  <Pagination.Prev
                    disabled={this.state.page <= 0}
                    onClick={e => this.handleClick(e, this.state.page - 1)}
                  />

                  {/*Eveything in between - see block before return statement*/}
                  {inbetween}

                  {/*Next Page*/}
                  <Pagination.Next
                    disabled={this.state.page >= this.state.total_pages - 1}
                    onClick={e => this.handleClick(e, this.state.page + 1)}
                  />

                  {/*Last Page*/}
                  <Pagination.Last
                    onClick={e => this.handleClick(e,this.state.total_pages - 1)}
                  />
                </Pagination>
              </Row>
            {/*Record Grid*/}


            </Container>

            <Container>
              {/*Record Grid*/}
              <Row className="justify-content-md-center" className="spacing">
              {this.state.objects.map((pollutant) => (
                 <Col className="spacing" lg="4" sm="6" xs="12">
                 <Card body style={{ backgroundColor: '#00', borderColor: '#2255' }}>
                   <CardImg src = {pollutant.chemspider_id}/>
                   <CardHeader>
                     {/* The dynamic link is arranged this way through the NavBar.js
                     and back to the Location component*/}
                     <h5><Link to={"/Pollutant/" + pollutant.chemical_name}>{pollutant.chemical_name}</Link></h5>
                   </CardHeader>
                   <CardBody>
                   <CardText>
                     <p>
                     <b>Inhalation Cancer Potency</b>
                     <br/>Potency: {pollutant.inhalation_cancer_potency} {pollutant.inhalation_cancer_potency_units}
                     <br/>Source: {pollutant.inhalation_cancer_potency_source}
                     <br/><b>Inhalation Reference Concentration</b>
                     <br/>Concentration: {pollutant.inhalation_reference_concentration} {pollutant.inhalation_reference_concentration_units}
                     <br/>Source: {pollutant.inhalation_reference_concentration_source}
                     <br/><b>National Ambient Air Quality Standards</b>
                     <br/>Standard: {pollutant.inhalation_reference_concentration} {pollutant.inhalation_reference_concentration_units}
                     <br/>Source: {pollutant.inhalation_reference_concentration_source}

                     {/*
                     County: {pollutant.pollutant_range}
                     <br/>Latitude: {pollutant.pollutant_range}
                     <br/>Longitude: {pollutant.pollutant_severity}
                     <br/>Population: {pollutant.pollutant_sources}
                     */}
                     </p>

                   </CardText>
                   </CardBody>
                </Card>
                 <p />
                 </Col>
              ))}
              </Row>
         </Container>
      </div>
      );
   }
}


