import React from "react";
import StateMap from "./StateMap";

import axios from "axios";




class Visuals extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      value: 0,
      illnessStateDict: {
        AL: 0,
        AK: 0,
        AZ: 0,
        AR: 0,
        CA: 0,
        CO: 0,
        CT: 0,
        DE: 0,
        FL: 0,
        GA: 0,
        HI: 0,
        ID: 0,
        IL: 0,
        IN: 0,
        IA: 0,
        KS: 0,
        KY: 0,
        LA: 0,
        ME: 0,
        MD: 0,
        MA: 0,
        MI: 0,
        MN: 0,
        MS: 0,
        MO: 0,
        MT: 0,
        NE: 0,
        NV: 0,
        NH: 0,
        NJ: 0,
        NM: 0,
        NY: 0,
        NC: 0,
        ND: 0,
        OH: 0,
        OK: 0,
        OR: 0,
        PA: 0,
        RI: 0,
        SC: 0,
        SD: 0,
        TN: 0,
        TX: 0,
        UT: 0,
        VT: 0,
        VA: 0,
        VI: 0,
        WA: 0,
        WV: 0,
        WI: 0,
        WY: 0
      },
      pollutantStateDict: {
        AL: 0,
        AK: 0,
        AZ: 0,
        AR: 0,
        CA: 0,
        CO: 0,
        CT: 0,
        DE: 0,
        FL: 0,
        GA: 0,
        HI: 0,
        ID: 0,
        IL: 0,
        IN: 0,
        IA: 0,
        KS: 0,
        KY: 0,
        LA: 0,
        ME: 0,
        MD: 0,
        MA: 0,
        MI: 0,
        MN: 0,
        MS: 0,
        MO: 0,
        MT: 0,
        NE: 0,
        NV: 0,
        NH: 0,
        NJ: 0,
        NM: 0,
        NY: 0,
        NC: 0,
        ND: 0,
        OH: 0,
        OK: 0,
        OR: 0,
        PA: 0,
        RI: 0,
        SC: 0,
        SD: 0,
        TN: 0,
        TX: 0,
        UT: 0,
        VT: 0,
        VA: 0,
        VI: 0,
        WA: 0,
        WV: 0,
        WI: 0,
        WY: 0
      },
      loaded: false
    };
  }



    updateCMapData(dataAll) {
      dataAll.data.forEach(element => {
        var updatedDict1 = { ...this.state.billStateDict };
        var updatedDict2 = { ...this.state.efpollutantStateDict };
        var updatedDict3 = { ...this.state.progStateDict };

        updatedDict1[element.abbr] = element.num_bills;
        updatedDict2[element.abbr] = element.num_orgs;
        updatedDict3[element.abbr] = element.num_progs;

        this.setState(prevState => ({
          billStateDict: updatedDict1,
          efpollutantStateDict: updatedDict2,
          progStateDict: updatedDict3
        }));
      });
    }

    scrapeProjectInfo() {
      axios
        .all([
          axios.get(`https://api.connectwithnature.me/park`),
          axios.get("https://api.connectwithnature.me/org"),
        ])
        .then(
          axios.spread((parkResponse, orgResponse) => {
            this.updateBarData(parkResponse, orgResponse);
            this.setState({
              loaded: true
            });
          })
        );
    }

    scrapeCustomerInfo() {
      axios.all([axios.get(`https://earth-first.live/api/states`)]).then(
        axios.spread(allResponse => {
          this.updateCMapData(allResponse);
        })
      );
    }

    componentDidMount() {
      this.scrapeProjectInfo();
      this.scrapeCustomerInfo();
    }



  handleChange = (event, value) => {
    this.setState({ value });
  };

  render() {
    const { classes } = this.props;
    const { illnessStateDict, pollutantStateDict, loaded } = this.state;

    const { billStateDict, efpollutantStateDict, progStateDict } = this.state;
    var data = [];
    if (illnessStateDict != null) {
      let temp = this.state.illnessStateDict;
      for (const [key, value] of Object.entries(temp)) {
        var tempDict = {};
        tempDict["label"] = key;
        tempDict["value"] = value;
        data.push(tempDict);
      }
    }
    var dataEF = [];
    if (efpollutantStateDict != null) {
      let temp = efpollutantStateDict;
      for (const [key, value] of Object.entries(temp)) {
        let tempDict = {};
        tempDict["label"] = key;
        tempDict["value"] = value;
        dataEF.push(tempDict);
      }
    }
    var dataOrg = [];
    if (pollutantStateDict != null) {
      let temp = this.state.pollutantStateDict;
      for (const [key, value] of Object.entries(temp)) {
        let tempDict = {};
        tempDict["label"] = key;
        tempDict["value"] = value;
        dataOrg.push(tempDict);
      }
    }
    if (loaded) {
      return (
        <div>
          <StateMap
            illnessData={illnessStateDict}
            pollutantData={pollutantStateDict}
            first="Illnesses:"
            second="Pollutants:"
          />
        </div>
      );
    }
    if (!loaded) {
      return (
        <div>
         HELLO!!!
        </div>
      );
    }
  }
}

export default Visuals;
