import React, { Component, useState, useEffect  } from 'react';
import 'bootstrap/dist/css/bootstrap.css';
import { Jumbotron, Container, Card, Button, CardImg, CardTitle, CardText, CardHeader,
         CardDeck, CardColumns, CardSubtitle, CardBody , NavLink, UncontrolledCarousel,
         Form,FormGroup, Label, Input, FormText} from 'reactstrap';
import { Image, Col, Row, Pagination, Nav, Tabs, Tab} from 'react-bootstrap';
import { BrowserRouter as Router, Route, Link , Switch, BrowserHistory } from 'react-router-dom';
import { createBrowserHistory } from 'history';
import { createUltimatePagination, ITEM_TYPES } from 'react-ultimate-pagination';
import ReactDOM from 'react-dom';
import Locations from './locations/Locations'


export class Search extends React.Component {


	constructor(props){
		super(props)	
		this.state = {
			searchstring : null		
		};
	}	

    componentDidMount() {
    	const {key} = this.props.match.params;
    	this.setState({searchstring : key})
    }

	render(){
    	const {key} = this.props.match.params;
		return(
	    	<div>
	    	{/*Make tabs not look shitty*/}
	    	<Tabs>
	    		<Tab.Content eventKey="location" title="Location" >
		    		<h1 style={{color: '#1976d2'}}>Locations Search Results</h1>
		    		<LocationResults fieldname = "city" searchstring = {key}/>		    		
	    		</Tab.Content>
	    		<Tab eventKey="illness" title="Illness">
		    		<h1 style={{color: '#1976d2'}}>Illnesses Search Results</h1>
		    		<IllnessResults fieldname = "illness_desc" searchstring = {key}/>		    				    		
	    		</Tab>
	    		<Tab eventKey="pollutants" title="Pollutants">
	    			<h1 style={{color: '#1976d2'}}>Polluants Search Results</h1>		
		    		<PollutantResults fieldname = "pollutant_name" searchstring = {key}/>		    				    			    			
	    		</Tab>
	      	</Tabs>
	      	</div>
		);
	}
}


export class LocationResults extends React.Component {

	constructor(props){
		super(props);
		this.state = {
			data : {},
			num_results: null,
			objects:[],
			page: null,
			total_pages: null
		};
	}

    componentWillMount() {
  		fetch(`https://api.howsmyair.me/location?q={%22filters%22:[{%22name%22:%22${this.props.fieldname}%22,%22op%22:%22like%22,%22val%22:%22%25${this.props.searchstring}%25%22}]}`)				
	       .then(res => res.json())
	       .then(data => { this.setState({
	       		data : data,
		        num_results: data.num_results,
		        objects: data.objects,
		        page: data.page-1,
		        total_pages: data.total_pages
       		})
    	})
  	};

  	render(){
  		return(
  			<div>
             <Container>
               {/*Record Grid*/}
               <Row className="justify-content-md-center" className="spacing">
               {this.state.objects.map((location) => (
                  <Col className="spacing" lg="4" sm="6" xs="12">
                  <Card body style={{ backgroundColor: '#00', borderColor: '#2255' }}>
                    <CardHeader>
                      {/* The dynamic link is arranged this way through the NavBar.js
                      and back to the Location component*/}
                      <h5><Link to={"/Location/" + location.id}>{location.city}</Link></h5>
                      <h6 class = "card-subtitle mb-2 text-muted">{location.state_name}</h6>
                    </CardHeader>
                    <CardBody>
                    <CardText>
                      <p>
                      County: {location.county_name}
                      <br/>Latitude: {location.latitude}
                      <br/>Longitude: {location.longitude}
                      <br/>Population: {location.population}
                      <br/>Density (per sq. mile): {location.density}
                      <br/>Time Zone: {location.timezone}
                      </p>
                    </CardText>
                    </CardBody>
                 </Card>
                  <p />
                  </Col>
               ))}
               </Row>
           </Container>             			
  			</div>
  		);
  	}
}

export class IllnessResults extends React.Component {

	constructor(props){
		super(props);
		this.state = {
			data : {},
			num_results: null,
			objects:[],
			page: null,
			total_pages: null
		};
	}

    componentWillMount() {
  		fetch(`https://api.howsmyair.me/illness?q={%22filters%22:[{%22name%22:%22${this.props.fieldname}%22,%22op%22:%22like%22,%22val%22:%22%25${this.props.searchstring}%25%22}]}`)				
	       .then(res => res.json())
	       .then(data => { this.setState({
	       		data : data,
		        num_results: data.num_results,
		        objects: data.objects,
		        page: data.page-1,
		        total_pages: data.total_pages
       		})
    	})
  	};

  	render(){
  		return(
  			<div>
             <Container>
              <Row className="justify-content-md-center" className="spacing">
              {this.state.objects.map((illness) => (
                 <Col className="spacing" lg="4" sm="6" xs="12">
                 <Card body style={{ backgroundColor: '#00', borderColor: '#2255' }}>
                    <CardImg top width="100%" src={illness.illness_image} />
                    <CardHeader>
                      {/* The dynamic link is arranged this way through the NavBar.js
                      and back to the Location component*/}
                      <h5><Link to={"/Illness/" + illness.illness_name}>{illness.illness_name}</Link></h5>
                    </CardHeader>
                    <CardBody>
                    <CardText>
	                  {illness.illness_desc}<br/>
                    </CardText>
                    </CardBody>
                 </Card>
                 <p />
                 </Col>
              ))}
              </Row>
           </Container>             			
  			</div>
  		);
  	}
}


export class PollutantResults extends React.Component {

	constructor(props){
		super(props);
		this.state = {
			data : {},
			num_results: null,
			objects:[],
			page: null,
			total_pages: null
		};
	}

    componentWillMount() {
  		fetch(`https://api.howsmyair.me/pollutants?q={%22filters%22:[{%22name%22:%22${this.props.fieldname}%22,%22op%22:%22like%22,%22val%22:%22%25${this.props.searchstring}%25%22}]}`)				
	       .then(res => res.json())
	       .then(data => { this.setState({
	       		data : data,
		        num_results: data.num_results,
		        objects: data.objects,
		        page: data.page-1,
		        total_pages: data.total_pages
       		})
    	})
  	};

  	render(){
  		return(
  			<div>
  			{this.state.num_results}
             <Container>
              <Row className="justify-content-md-center" className="spacing">
              {this.state.objects.map((pollutant) => (
                 <Col className="spacing" lg="4" sm="6" xs="12">
                 <Card body style={{ backgroundColor: '#00', borderColor: '#2255' }}>
                   <CardImg src = {pollutant.chemspider_id}/>
                   <CardHeader>
                     {/* The dynamic link is arranged this way through the NavBar.js
                     and back to the Location component*/}
                     <h5><Link to={"/Pollutant/" + pollutant.chemical_name}>{pollutant.chemical_name}</Link></h5>
                   </CardHeader>
                   <CardBody>
                   <CardText>
                     <p>
                     <b>Inhalation Cancer Potency</b>
                     <br/>Potency: {pollutant.inhalation_cancer_potency} {pollutant.inhalation_cancer_potency_units}
                     <br/>Source: {pollutant.inhalation_cancer_potency_source}
                     <br/><b>Inhalation Reference Concentration</b>
                     <br/>Concentration: {pollutant.inhalation_reference_concentration} {pollutant.inhalation_reference_concentration_units}
                     <br/>Source: {pollutant.inhalation_reference_concentration_source}
                     <br/><b>National Ambient Air Quality Standards</b>
                     <br/>Standard: {pollutant.inhalation_reference_concentration} {pollutant.inhalation_reference_concentration_units}
                     <br/>Source: {pollutant.inhalation_reference_concentration_source}

                     {/*
                     County: {pollutant.pollutant_range}
                     <br/>Latitude: {pollutant.pollutant_range}
                     <br/>Longitude: {pollutant.pollutant_severity}
                     <br/>Population: {pollutant.pollutant_sources}
                     */}
                     </p>

                   </CardText>
                   </CardBody>
                </Card>
                 <p />
                 </Col>
              ))}
              </Row>
           </Container>             			
  			</div>
  		);
  	}
}
