import React, { Component } from 'react';
import 'bootstrap/dist/css/bootstrap.css';
import { Jumbotron, Container, Card, Button, CardImg, CardTitle, CardText, CardHeader,
         CardDeck, CardColumns, CardSubtitle, CardBody , NavLink, UncontrolledCarousel,
         Form,FormGroup, Label, Input, FormText} from 'reactstrap';
import { Image, Col, Row, Pagination, Nav, Tabs, Tab} from 'react-bootstrap';
import { BrowserRouter as Router, Route, Link , Switch, BrowserHistory } from 'react-router-dom';
import { createBrowserHistory } from 'history';
import { createUltimatePagination, ITEM_TYPES } from 'react-ultimate-pagination';
import ReactDOM from 'react-dom';
import Locations from './locations/Locations'


export class Search extends React.Component {


	constructor(props){
		super(props)	
		this.state = {
			searchstring : null		
		};
	}	

    componentDidMount() {
    	const {key} = this.props.match.params;
    	this.setState({searchstring : key})
    }

	render(){
		return(
	    	<div>
	    	{/*Make tabs not look shitty*/}
	    	<Tabs>
	    		<Tab.Content eventKey="location" title="Location">
		    		<h1>Locations Search Results</h1>
		    		<LocationResults searchstring = {this.state.searchstring}/>
	    		</Tab.Content>
	    		<Tab eventKey="illness" title="Illness">
		    		<h1>Illnesses Search Results</h1>
	    		</Tab>
	    		<Tab eventKey="pollutants" title="Pollutants">
	    			<h1>Polluants Search Results</h1>		
	    		</Tab>
	      	</Tabs>
	      	</div>
		);
	}
}


export class LocationResults extends React.Component {

	constructor(props){
		super(props);
		this.state = {
			data : {},
			num_results: null,
			objects:[],
			page: null,
			total_pages: null,
		    endpoint : "https://api.howsmyair.me/location",
		    searchstring : props.searchstring
		};
	}


	componentWillMount(){
		this.setState({searchstring : this.props.searchstring})
	}

    componentDidMount() {
  		fetch(`https://api.howsmyair.me/location?q={%22filters%22:[{%22name%22:%22city%22,%22op%22:%22like%22,%22val%22:%22%25${this.state.searchstring}%25%22}]}`)				
	       .then(res => res.json())
	       .then(data => { this.setState({
	       		data : data,
		        num_results: data.num_results,
		        objects: data.objects,
		        page: data.page-1,
		        total_pages: data.total_pages
       		})
    	})
  	};

  	render(){
  		return(
  			<div>
  				{this.state.num_results}
  				<h1>{this.props.searchstring}</h1>
  				<h1>{this.state.req}</h1>  				
  				{this.state.endpoint + "?q={%22filters%22:[{%22name%22:%22city%22,%22op%22:%22like%22,%22val%22:%22%25" +
			this.props.searchstring +"%25%22}]}"}
  			</div>
  		);
  	}


}
