import React, { Component } from 'react';
import 'bootstrap/dist/css/bootstrap.css';
import { Jumbotron, Container, Card, Button, CardImg, CardTitle, CardText, CardHeader,
         CardDeck, CardColumns, CardSubtitle, CardBody , NavLink, UncontrolledCarousel,
         Form,FormGroup, Label, Input, FormText} from 'reactstrap';
import { Image, Col, Row, Pagination} from 'react-bootstrap';
import { BrowserRouter as Router, Route, Link , Switch,BrowserHistory } from 'react-router-dom';
import { createBrowserHistory } from 'history';
import { createUltimatePagination, ITEM_TYPES } from 'react-ultimate-pagination';
import ReactDOM from 'react-dom';


const ENDPOINT = 'https://api.howsmyair.me/pollutants';

class PollutantInstance extends Component {

   constructor(props) {
     super(props);
     this.state = {
      data: {},
      related_illnesses: [],
      related_locations: []
     };
   }

  componentDidMount() {
     fetch(ENDPOINT + '/' + this.props.pollutant_name)
        .then(res => res.json())
        .then(data => { this.setState({
           data: data,
           related_illnesses : data.related_illnesses,
           related_locations : data.related_locations
        })
     })
   };

  render() {
     return (
      <div>
      <a style={{ cursor: 'pointer' }} href={"/Pollutant/" + this.state.data.chemical_name}>
      <Card body style={{ backgroundColor: '#00', borderColor: '#2255', height: '42rem' }}>
        <CardImg src = {this.state.data.chemspider_id}/>
        <CardHeader>
          {/* The dynamic link is arranged this way through the NavBar.js
          and back to the Location component*/}
          <h5>{this.state.data.chemical_name}</h5>
        </CardHeader>
        <CardBody>
        <CardText>
          <p>
          <b>Inhalation Cancer Potency</b>
          <br/>Potency: {this.state.data.inhalation_cancer_potency} {this.state.data.inhalation_cancer_potency_units}
          <br/>Source: {this.state.data.inhalation_cancer_potency_source}
          <br/><b>Inhalation Reference Concentration</b>
          <br/>Concentration: {this.state.data.inhalation_reference_concentration} {this.state.data.inhalation_reference_concentration_units}
          <br/>Source: {this.state.data.inhalation_reference_concentration_source}
          <br/><b>National Ambient Air Quality Standards</b>
          <br/>Standard: {this.state.data.national_ambient_air_quality_std} {this.state.data.national_ambient_air_quality_std_units}
          <br/>Source: {this.state.data.national_ambient_air_quality_std_source}
          </p>
        </CardText>
        </CardBody>
     </Card>
     </a>
      </div>
     );
  }
}

export default PollutantInstance;
