import React, { Component } from 'react';
import 'bootstrap/dist/css/bootstrap.css';
import { Jumbotron, Container, Card, Button, CardImg, CardTitle, CardText, CardHeader,
         CardDeck, CardColumns, CardSubtitle, CardBody , NavLink, UncontrolledCarousel,
         Form,FormGroup, Label, Input, FormText} from 'reactstrap';
import { Image, Col, Row, Pagination, DropdownButton, Dropdown, ButtonToolbar } from 'react-bootstrap';
import { BrowserRouter as Router, Route, Link , Switch,BrowserHistory } from 'react-router-dom';
import { createBrowserHistory } from 'history';
import { createUltimatePagination, ITEM_TYPES } from 'react-ultimate-pagination';
import ReactDOM from 'react-dom';
import Illnesses from '../illnesses/Illnesses';
import IllnessInstance from '../illnesses/IllnessInstance';
import { Locations, Location } from '../locations/Locations';
import LocationInstance from '../locations/LocationInstance';

/**
Sources:

Pagination sources:
https://github.com/react-bootstrap/pagination
https://www.npmjs.com/package/react-ultimate-pagination
https://codepen.io/joedazza/pen/BVXVej
**/

const ENDPOINT = 'https://api.howsmyair.me/pollutants';

export class Pollutant extends Component {

    constructor(props) {
      super(props);
      this.state = {
        data: {},
        related_illnesses: [],
        related_locations: []
      };
    }

   componentDidMount() {
      const {key} = this.props.match.params;
      // get location
      fetch(ENDPOINT + '/' + key)
         .then(res => res.json())
         .then(data => { this.setState({
            data: data,
            related_illnesses : data.related_illnesses,
            related_locations : data.related_locations
         })
      })
    };

   render() {
      return (
        <div>
            <Container>
            <p>
            <br/>
            <h1>{this.state.data.chemical_name}</h1>
            <br/>
            <b>Inhalation Cancer Potency</b>
            <br/>Potency: {this.state.data.inhalation_cancer_potency}
            <br/>Units: {this.state.data.inhalation_cancer_potency_units}
            <br/>Source: {this.state.data.inhalation_cancer_potency_source}
            <br/><b>Inhalation Reference Concentration</b>
            <br/>Concentration: {this.state.data.inhalation_reference_concentration}
            <br/>Units: {this.state.data.inhalation_reference_concentration_units}
            <br/>Source: {this.state.data.inhalation_reference_concentration_source}
            <br/><b>National Ambient Air Quality Standards</b>
            <br/>Standard: {this.state.data.national_ambient_air_quality_std}
            <br/>Units: {this.state.data.national_ambient_air_quality_std_units}
            <br/>Source: {this.state.data.national_ambient_air_quality_std_source}
            <img src = {this.state.data.chemspider_id}/>
            </p>


            <h1 style={{color: '#1976d2'}}>{this.state.data.chemical_name}</h1>
            <br/>
            <Row>
              <Col>
              <ListGroup variant="flush" >
                <ListGroup.Item  style={{color: '#1976d2'}}>
                  Inhalation Cancer Potency: {this.state.data.inhalation_cancer_potency} {this.state.data.inhalation_cancer_potency_units}
                  <i>Source: {this.state.data.inhalation_cancer_potency_source}</i>
                </ListGroup.Item>
                <ListGroup.Item style={{color: '#1976d2'}}>
                  Symptoms: {this.state.data.illness_symptoms}
                </ListGroup.Item>
                <ListGroup.Item style={{color: '#1976d2'}}>
                  Contagious: {this.state.data.illness_contagious}
                </ListGroup.Item>
                <ListGroup.Item style={{color: '#1976d2'}}>
                  Treatable: {this.state.data.illness_treatable}
                </ListGroup.Item>
              </ListGroup>
              </Col>
              <Col>
               <img width="50%" src={this.state.data.illness_image} alt="Italian Trulli"/>
              </Col>
            </Row>
            </Container>
            {/*related locations*/}
            <Container>
            <h6>Related Illnesses</h6>
            <Jumbotron>
            <Row className="justify-content-md-center" className="spacing">
            {this.state.related_illnesses.map((illness) => (
               <Col className="spacing" lg="4" sm="6" xs="12">
                  <IllnessInstance illness_name={illness.illness_name} />
               </Col>
            ))}
            </Row>
            </Jumbotron>
            </Container>
            {/*related pollutants*/}
            <Container>
            <h6>Related Locations</h6>
            <Jumbotron>
            <Row className="justify-content-md-center" className="spacing">
            {this.state.related_locations.map((location) => (
               <Col className="spacing" lg="4" sm="6" xs="12">
                  <LocationInstance location_id={location.location_id} />
                  <p />
              </Col>
            ))}
            </Row>
            </Jumbotron>
            </Container>
        </div>
      );
   }
}

const buildOrder  = (fieldname,direction) => {
    return `{%22field%22:%22${fieldname}%22,%22direction%22:%22${direction}%22}`
}

const buildFilter = (fieldname,operator,value) => {
    return `{%22name%22:%22${fieldname}%22,%22op%22:%22${operator}%22,%22val%22:%22${value}%22}`
}

const buildQuery = (model,page,filterObjs,orderObjs) => {
      return `https://api.howsmyair.me/${model}?page=${page}&q={%22filters%22:[${filterObjs}],%22order_by%22:[${orderObjs}]}`
}

export class Pollutants extends Component {

  constructor(props){
    super(props);
    this.state = {
      data : {},
      num_results: null,
      objects:[],
      page: null,
      total_pages: null,
      states : [],
      time_zones : [],
      inhalation_cancer_potency : "None",
      inhalation_cancer_potency_source : "None",
      inhalation_cancer_potency_units : "None",
      inhalation_reference_concentration : "None",
      inhalation_reference_concentration_source : "None",
      inhalation_reference_concentration_units : "None",
      national_ambient_air_quality_std : "None",
      national_ambient_air_quality_std_source : "None",
      national_ambient_air_quality_std_units : "None",
      cancer_potency_sources : [],
      cancer_potency_units : [],
      reference_concentration_source : [],
      reference_concentration_units : [],
      ambient_air_quality_std_source : [],
      ambient_air_quality_std_units : [],
      sort_by : "chemical_name",
      display_sort : "Chemical Name",
