import React, { Component } from 'react';
import 'bootstrap/dist/css/bootstrap.css';
import { Jumbotron, Container, Card, Button, CardImg, CardTitle, CardText, CardHeader,
         CardDeck, CardColumns, CardSubtitle, CardBody , NavLink, Carousel,
         Form,FormGroup, Label, Input, FormText} from 'reactstrap';
import { Image, Col, Row, Pagination, DropdownButton, Dropdown, ButtonToolbar, ListGroup } from 'react-bootstrap';
import { BrowserRouter as Router, Route, Link , Switch,BrowserHistory } from 'react-router-dom';
import { createBrowserHistory } from 'history';
import ReactDOM from 'react-dom';
import Locations from '../locations/Locations';
import LocationInstance, {RelatedLocations} from '../locations/LocationInstance';
import Pollutants from '../pollutants/Pollutants';
import PollutantInstance,{RelatedPollutants} from '../pollutants/PollutantInstance';
const fetch = require("node-fetch");

/**
Sources:

Pagination sources:
https://github.com/react-bootstrap/pagination
https://www.npmjs.com/package/react-ultimate-pagination
https://codepen.io/joedazza/pen/BVXVej
**/

const ENDPOINT = 'https://api.howsmyair.me/illness';


export class Illness extends Component {

    constructor(props) {
      super(props);
      this.state = {
        data: {},
        isloaded : false,        
        related_locations: [],
        related_pollutants: []
      };
      this.componentDidMount = this.componentDidMount.bind(this)
    }
   componentDidMount() {
      const {key} = this.props.match.params;
      // get location
      fetch(ENDPOINT + '/' + key)
         .then(res => res.json())
         .then(data => { this.setState({
            data: data,
            isloaded : true,                                        
            related_locations : data.related_locations,
            related_pollutants : data.related_pollutants
         })
      })
    };
   render() {
      if(this.state.isloaded){    
      return (
        <div>
          <Jumbotron>
            <Container>
            <Row>
              <Col>
              <h1 style={{color: '#1976d2'}}>{this.state.data.illness_name}</h1>
              <h6 class="card-subtitle mb-2 text-muted">{this.state.data.illness_desc}</h6>
              <br/>
                <ListGroup variant="flush" >
                <ListGroup.Item  style={{color: '#1976d2'}}>
                  Lethality: {this.state.data.illness_lethality}
                </ListGroup.Item>
                <ListGroup.Item style={{color: '#1976d2'}}>
                  Symptoms: {this.state.data.illness_symptoms}
                </ListGroup.Item>
                <ListGroup.Item style={{color: '#1976d2'}}>
                  Contagious: {this.state.data.illness_contagious}
                </ListGroup.Item>
                <ListGroup.Item style={{color: '#1976d2'}}>
                  Treatable: {this.state.data.illness_treatable}
                </ListGroup.Item>
              </ListGroup>
              </Col>
              <Col>
               <img width="70%" src={this.state.data.illness_image} alt="Italian Trulli"/>
              </Col>
            </Row>
            </Container>
          </Jumbotron>

          <Container>
            <Row>
              <Col>
                <h1 style={{color: '#1976d2'}}>Related Locations</h1><br/>
                <Row className="justify-content-md-center" className="spacing">
                {this.state.related_locations.map((location) => (
                   <Col className="spacing" lg="4" sm="6" xs="12">
                      <RelatedLocations location_id={location.location_id} />
                      <p />
                  </Col>
                ))}
                </Row>
              </Col>
            </Row>
          </Container>
          <Container>
            <Row>
              <Col>
                <h1 style={{color: '#1976d2'}}>Related Pollutants</h1><br/>
                <Row className="justify-content-md-center" className="spacing">
                {this.state.related_pollutants.map((pollutant) => (
                   <Col className="spacing" lg="4" sm="6" xs="12">
                    <RelatedPollutants pollutant_name={pollutant.pollutant_name} />
                    <p />
                 </Col>
                ))}
                </Row>            
              </Col>
            </Row>
          </Container>


        </div>
      );
    } else {
      this.componentDidMount();
      return(
        <div>
         <center><img src = "https://i.makeagif.com/media/9-30-2017/bhAyTs.gif"/></center>
        </div>
      )
    }
  }
}

const buildOrder  = (fieldname,direction) => {
    return `{%22field%22:%22${fieldname}%22,%22direction%22:%22${direction}%22}`
}

const buildFilter = (fieldname,operator,value) => {
    return `{%22name%22:%22${fieldname}%22,%22op%22:%22${operator}%22,%22val%22:%22${value}%22}`
}

const buildQuery = (model,page,filterObjs,orderObjs) => {
      return `https://api.howsmyair.me/${model}?page=${page}&q={%22filters%22:[${filterObjs}],%22order_by%22:[${orderObjs}]}`
}

export class Illnesses extends Component {

  constructor(props){
    super(props);
    this.state = {
      data : {},
      num_results: null,
      objects:[],
      page: null,
      total_pages: null,
      states : [],
      time_zones : [],
      current_contagious: "None",
      current_lethality : "None",
      current_treatable : "None",
      sort_by : "illness_name",
      display_sort : "Illness",
      order : "asc",
      display_order : "Ascending",
      curent_page: 0,
      isloaded : false,
    };
        this.getQuery = this.getQuery.bind(this)
        this.componentDidMount = this.componentDidMount.bind(this)
  }

    componentDidMount() {
      console.log(this.getQuery())
      fetch(this.getQuery())
         .then(res => res.json())
         .then(data => {
          this.setState({
            data : data,
            num_results: data.num_results,
            objects: data.objects,
            curent_page: data.page-1,
            page: data.page-1,
            total_pages: data.total_pages,
            isloaded : true,
          })

      })
    };

    getQuery(){

      let filterObjs = buildFilter("illness_name","like",`%25%25`);
      let orderObjs = buildOrder("illness_name","asc")

      if (this.state.current_contagious != "None") {
        filterObjs += ","+buildFilter("illness_contagious","eq",this.state.current_contagious)
      }
      if (this.state.current_lethality != "None") {
        filterObjs += ","+buildFilter("illness_lethality","eq",this.state.current_lethality)
      }
      if (this.state.current_treatable != "None") {
        filterObjs += ","+buildFilter("illness_treatable","eq",this.state.current_treatable)
      }

      orderObjs = buildOrder(this.state.sort_by,this.state.order)

      return buildQuery("illness",this.state.curent_page+1,filterObjs,orderObjs);
    }

    render(){
      if (this.state.isloaded){
      console.log(this.state.curent_page)
      console.log(this.state.page)
      console.log(this.state.total_pages)
      console.log(this.state.num_results)
      let span = 4
      let page = this.state.page
      let end = this.state.total_pages - 1
      let l = page - (span / 2)
      let r = page + (span / 2)
      let inbetween = [];

      if (this.state.total_pages < span + 1){
          for (let p = 0; p < this.state.total_pages; p++) {
            inbetween.push(
              <Pagination.Item
                active={p === page}
                onClick={e => this.setState({ curent_page : p , isloaded : false})}
              >
              {p + 1}
              </Pagination.Item>
            );
          }
      } else if (page-span < 0) {
        for (let p = 0; p <= span; p++) {
          inbetween.push(
            <Pagination.Item
              active={p === page}
              onClick={e => this.setState({ curent_page : p , isloaded : false})}
            >
            {p + 1}
            </Pagination.Item>
          );
        }
      } else if (page + span > end) {
          for (let p = end - span; p <= end ; p++) {
            inbetween.push(
              <Pagination.Item
                active={p === page}
              onClick={e => this.setState({ curent_page : p , isloaded : false})}
              >
              {p + 1}
              </Pagination.Item>
          );
        }
      } else {
        for (let p = l < 0 ? 0 : l; p <= r && p <= end; p++) {
          inbetween.push(
            <Pagination.Item
              active={p === page}
              onClick={e => this.setState({ curent_page : p , isloaded : false})}
            >
            {p + 1}
            </Pagination.Item>
          );
        }
      }
      return(
        <div>
          <br/>
            <Row className="justify-content-md-center">
                <h1 style={{color: '#1976d2'}}>{this.state.num_results} Illnesses</h1>
            </Row>
            <Jumbotron>
            <Row className="justify-content-md-center">
              <Col md="auto">
                <h3 style={{color: '#1976d2'}}>Filters</h3>
              </Col>
              <Col md="auto">
                <center>
                  <DropdownButton id="dropdown-basic-button" title="Contagious">
                    <Dropdown.Item onClick={() => {this.setState({ current_contagious : "None", curent_page : 0, isloaded : false});}}>
                      None
                    </Dropdown.Item>
                    <Dropdown.Item onClick={() => {this.setState({ current_contagious : "yes", curent_page : 0, isloaded : false});}}>
                      Yes
                    </Dropdown.Item>
                    <Dropdown.Item onClick={() => {this.setState({ current_contagious : "no", curent_page : 0, isloaded : false});}}>
                      No
                    </Dropdown.Item>
                  </DropdownButton>
                  <h5 style={{color: '#1976d2'}}>{this.state.current_contagious}</h5>
                </center>
              </Col>
              <Col md="auto">
                <center>
                  <DropdownButton id="dropdown-basic-button" title="Treatable">
                    <Dropdown.Item onClick={() => {this.setState({ current_lethality : "None", curent_page : 0, isloaded : false});}}>
                      None
                    </Dropdown.Item>
                    <Dropdown.Item onClick={() => {this.setState({ current_lethality : "lethal", curent_page : 0, isloaded : false});}}>
                      Yes
                    </Dropdown.Item>
                    <Dropdown.Item onClick={() => {this.setState({ current_lethality : "non-lethal", curent_page : 0, isloaded : false});}}>
                      No
                    </Dropdown.Item>
                  </DropdownButton>
                  <h5 style={{color: '#1976d2'}}>{this.state.current_contagious}</h5>
                </center>
              </Col>
              <Col md="auto">
                <center>
                  <DropdownButton id="dropdown-basic-button" title="Treatable">
                    <Dropdown.Item onClick={() => {this.setState({ current_treatable : "None", curent_page : 0, isloaded : false});}}>
                      None
                    </Dropdown.Item>
                    <Dropdown.Item onClick={() => {this.setState({ current_treatable : "yes", curent_page : 0, isloaded : false});}}>
                      Yes
                    </Dropdown.Item>
                    <Dropdown.Item onClick={() => {this.setState({ current_treatable : "no", curent_page : 0, isloaded : false});}}>
                      No
                    </Dropdown.Item>
                  </DropdownButton>
                  <h5 style={{color: '#1976d2'}}>{this.state.current_treatable}</h5>
                </center>
              </Col>
              <h3 style={{color: '#1976d2'}}><center>Sort By</center></h3>
              <Col md="auto">
                  <DropdownButton id="dropdown-basic-button" title={this.state.display_sort}>
                    <Dropdown.Item onClick={() => {this.setState({ sort_by : "illness_name", display_sort : "Illness", curent_page : 0, isloaded : false});}}>
                      Illness
                    </Dropdown.Item>
                    <Dropdown.Item onClick={() => {this.setState({ sort_by : "illness_lethality", display_sort : "Lethality", curent_page : 0, isloaded : false});}}>
                      Lethality
                    </Dropdown.Item>
                    <Dropdown.Item onClick={() => {this.setState({ sort_by : "illness_contagious", display_sort : "Contagious", curent_page : 0, isloaded : false});}}>
                      Contagious
                    </Dropdown.Item>
                    <Dropdown.Item onClick={() => {this.setState({ sort_by : "illness_treatable", display_sort : "Treatable", curent_page : 0, isloaded : false});}}>
                      Treatable
                    </Dropdown.Item>
                  </DropdownButton>
              </Col>
              <Col md="auto">
                  <DropdownButton id="dropdown-basic-button" title = {this.state.display_order}>
                    <Dropdown.Item onClick={() => {this.setState({ order : "asc", display_order : "Ascending", curent_page : 0, isloaded : false});}}>
                      Ascending
                    </Dropdown.Item>
                    <Dropdown.Item onClick={() => {this.setState({ order : "desc", display_order : "Descending", curent_page : 0, isloaded : false});}}>
                      Descending
                    </Dropdown.Item>
                  </DropdownButton>
              </Col>
            </Row>
            </Jumbotron>
          <br/>
          <Container>
            <center><h4 style={{color: '#1976d2'}}>Page {this.state.curent_page+1}/{this.state.total_pages}</h4></center>
            <Row className="justify-content-md-center">
              <Pagination>
                {/*First Page*/}
                <Pagination.First
                  onClick={e => this.setState({ curent_page : 0, isloaded : false})}
                />

                {/*Previous Page*/}
                <Pagination.Prev
                  disabled={this.state.page <= 0}
                  onClick={e => this.setState({ curent_page : this.state.page -1 , isloaded : false})}
                />

                {/*Eveything in between - see block before return statement*/}
                {inbetween}

                {/*Next Page*/}
                <Pagination.Next
                  disabled={this.state.page >= this.state.total_pages - 1}
                  onClick={e => this.setState({ curent_page : this.state.page +1 , isloaded : false})}
                />

                {/*Last Page*/}
                <Pagination.Last
                  onClick={e => this.setState({ curent_page : this.state.total_pages - 1 , isloaded : false})}
                />
              </Pagination>
            </Row>
           </Container>
            <Container>
              {/*Record Grid*/}
              <Row className="justify-content-md-center" className="spacing">
              {this.state.objects.map((illness) => (
                 <Col className="spacing" lg="4" sm="6" xs="12">
                 <a style={{ cursor: 'pointer' }} href={"/Illness/" + illness.illness_name}>
                 <Card body style={{ backgroundColor: '#00', borderColor: '#2255', height: '35rem'}}>
                    <CardImg style={{ maxHeight: '16rem' }} src={illness.illness_image} />
                    <CardHeader>
                      {/* The dynamic link is arranged this way through the NavBar.js
                      and back to the Location component*/}
                      <h5>{illness.illness_name}</h5>
                    </CardHeader>
                    <CardBody>
                    <CardText>
                      <p>Lethality: {illness.illness_lethality}
                      <br/>Symptoms: {illness.illness_symptoms}
                      <br/>Contagious: {illness.illness_contagious}
                      <br/>Treatable: {illness.illness_treatable}
                      </p>
                    </CardText>
                    </CardBody>
                 </Card>
                 </a>
                 <p />
                 </Col>
              ))}
              </Row>
         </Container>
        </div>
      );
    } else {
      this.componentDidMount();
      return (
        <div>
         <center><img src = "https://i.makeagif.com/media/9-30-2017/bhAyTs.gif"/></center>
        </div>
      );
   }
  }
}
