import React, { Component } from 'react';
import 'bootstrap/dist/css/bootstrap.css';
import { Jumbotron, Container, Card, Button, CardImg, CardTitle, CardText, CardHeader,
         CardDeck, CardColumns, CardSubtitle, CardBody , NavLink, UncontrolledCarousel,
         Form,FormGroup, Label, Input, FormText, DropdownButton } from 'reactstrap';
import { Image, Col, Row, Pagination} from 'react-bootstrap';
import { BrowserRouter as Router, Route, Link , Switch,BrowserHistory } from 'react-router-dom';
import { createBrowserHistory } from 'history';
import { createUltimatePagination, ITEM_TYPES } from 'react-ultimate-pagination';
import ReactDOM from 'react-dom';
import Illnesses from '../illnesses/Illnesses';
import Pollutants from '../pollutants/Pollutants';
import PollutantInstance from '../pollutants/PollutantInstance';
import IllnessInstance from '../illnesses/IllnessInstance';

/**
Sources:

Pagination sources:
https://github.com/react-bootstrap/pagination
https://www.npmjs.com/package/react-ultimate-pagination
https://codepen.io/joedazza/pen/BVXVej
**/

const ENDPOINT = 'https://api.howsmyair.me/location';

export class Location extends Component {

   constructor(props) {
      super(props);
      this.state = {
        data: {},
        location_url : null,
        related_illnesses: [],
        related_pollutants: []
      };
   }

   componentDidMount() {

      const {key} = this.props.match.params;

      // get location
      fetch(ENDPOINT + '/' + key)
         .then(res => res.json())
         .then(data => { this.setState({
            data: data,
            location_url : "https://maps.googleapis.com/maps/api/staticmap?center="+
                            data.latitude+","+data.longitude+
                            "&zoom=12&size=400x400"+
                            "&markers=color:green%7Clabel:G%7C"+
                            data.latitude+","+data.longitude +
                            "&key=AIzaSyB-U0Z7cqufI-MHmRwfLmgqDCyQmOlIq48",
            related_illnesses : data.related_illnesses,
            related_pollutants : data.related_pollutants
         })
      })
   };

   render() {
      return (
        <div>
            <Container>
            <p>
            <br/>
            <h1>{this.state.data.city}</h1>
            <h2 class = "card-subtitle mb-2 text-muted">{this.state.data.state_name}</h2>
            <br/>
            County: {this.state.data.county_name}
            <br/>Latitude: {this.state.data.latitude}
            <br/>Longitude: {this.state.data.longitude}
            <br/>Population: {this.state.data.population}
            <br/>Density (per sq. mile): {this.state.data.density}
            <br/>Time Zone: {this.state.data.timezone}
            </p>
            <img src={this.state.location_url} alt="Italian Trulli"/>
            </Container>

            {/*related illnessess*/}
            <Container>
            <Jumbotron>
            <h6> Related Illnesses </h6>
            {this.state.related_illnesses.map((illness) => (
               <CardColumns>
                  <IllnessInstance illness_name={illness.illness_name} />
               </CardColumns>
            ))}
            </Jumbotron>
            </Container>

            {/*related pollutants*/}
            <Container>
            <Jumbotron>
            <h6> Related Polluants </h6>
            {this.state.related_pollutants.map((pollutant) => (
               <CardColumns>
                 <PollutantInstance pollutant_name={pollutant.pollutant_name} />
              </CardColumns>
            ))}
            </Jumbotron>
            </Container>
        </div>
      );
   }
}

export class Locations extends Component {

   /* This right here is like object attributes */
  constructor(props) {
    super(props);
    this.state = {
      num_results: null,
      objects: [],
      page: null,
      total_pages: null,
      req : null
    }
  }

  /* When the component is loaded, here's init */
  componentDidMount() {
    fetch(ENDPOINT)
       .then(res => res.json())
       .then(data => { this.setState({
          num_results: data.num_results,
          objects: data.objects,
          page: data.page-1,
          total_pages: data.total_pages
       })
    })
  }

  /* Here's how we are going to handle clicks for pagination
     Really only two things about the current state changes:
     the current page numbers, and the results shown
  */
   handleClick(e, index) {
      e.preventDefault();
      fetch(ENDPOINT + "?page=" + (index + 1))
         .then(res => res.json())
         .then(data => { this.setState({
            num_results: data.num_results,
            objects: data.objects,
            page: data.page - 1,
            total_pages: data.total_pages
         })
      })
   }


   /* Here's how the Locations Page is rendered*/
   /* Source: looking at example of pagination for Global Antidote
      https://gitlab.com/stubbsnc/globalantidote/blob/production/dynamic-site/src/pages*/
   render() {
      let span = 4
      let page = this.state.page
      let end = this.state.total_pages - 1
      let l = page - (span / 2)
      let r = page + (span / 2)
      let inbetween = [];

      if (page-span < 0) {
        for (let p = 0; p <= span; p++) {
          inbetween.push(
            <Pagination.Item
              active={p === page}
              onClick={e => this.handleClick(e,p)}
            >
            {p + 1}
            </Pagination.Item>
          );
        }
      } else if (page + span > end) {
          for (let p = end - span; p <= end ; p++) {
            inbetween.push(
              <Pagination.Item
                active={p === page}
                onClick={e => this.handleClick(e,p)}
              >
              {p + 1}
              </Pagination.Item>
          );
        }
      } else {
        for (let p = l < 0 ? 0 : l; p <= r && p <= end; p++) {
          inbetween.push(
            <Pagination.Item
              active={p === page}
              onClick={e => this.handleClick(e, p)}
            >
            {p + 1}
            </Pagination.Item>
          );
        }
      }

      return (
      <div>
            {/*Request Stats*/}
            <center>
               <h1>Locations</h1>
               {this.state.num_results} Results,
                Page {this.state.page + 1} / {this.state.total_pages}
            </center>

            {/*Filter params*/}

            <DropdownButton id="dropdown-basic-button" title="Dropdown button">
              <Dropdown.Item href="#/action-1">Action</Dropdown.Item>
              <Dropdown.Item href="#/action-2">Another action</Dropdown.Item>
              <Dropdown.Item href="#/action-3">Something else</Dropdown.Item>
            </DropdownButton>

            <Container>
              <Row className="justify-content-md-center">
                <Pagination>
                  {/*First Page*/}
                  <Pagination.First
                    onClick={e => this.handleClick(e, 0)}
                  />

                  {/*Previous Page*/}
                  <Pagination.Prev
                    disabled={this.state.page <= 0}
                    onClick={e => this.handleClick(e, this.state.page - 1)}
                  />

                  {/*Eveything in between - see block before return statement*/}
                  {inbetween}

                  {/*Next Page*/}
                  <Pagination.Next
                    disabled={this.state.page >= this.state.total_pages - 1}
                    onClick={e => this.handleClick(e, this.state.page + 1)}
                  />

                  {/*Last Page*/}
                  <Pagination.Last
                    onClick={e => this.handleClick(e,this.state.total_pages - 1)}
                  />
                </Pagination>
              </Row>
             </Container>

             <Container>
               {/*Record Grid*/}
               <Row className="justify-content-md-center" className="spacing">
               {this.state.objects.map((location) => (
                  <Col className="spacing" lg="4" sm="6" xs="12">
                  <Card body style={{ backgroundColor: '#00', borderColor: '#2255' }}>
                  <CardImg src={
                     "https://maps.googleapis.com/maps/api/staticmap?center="+
                                     location.latitude+","+location.longitude+
                                     "&zoom=12&size=400x400"+
                                     "&markers=color:green%7Clabel:G%7C"+
                                     location.latitude+","+location.longitude +
                                     "&key=AIzaSyB-U0Z7cqufI-MHmRwfLmgqDCyQmOlIq48"}

                                     alt="Italian Trulli" />
                    <CardHeader>
                      {/* The dynamic link is arranged this way through the NavBar.js
                      and back to the Location component*/}
                      <h5><Link to={"/Location/" + location.id}>{location.city}</Link></h5>
                      <h6 class = "card-subtitle mb-2 text-muted">{location.state_name}</h6>
                    </CardHeader>
                    <CardBody>
                    <CardText>
                      <p>
                      County: {location.county_name}
                      <br/>Latitude: {location.latitude}
                      <br/>Longitude: {location.longitude}
                      <br/>Population: {location.population}
                      <br/>Density (per sq. mile): {location.density}
                      <br/>Time Zone: {location.timezone}
                      </p>
                    </CardText>
                    </CardBody>
                 </Card>
                  <p />
                  </Col>
               ))}
               </Row>
           </Container>
      </div>
      );
   }
}


